INSERT INTO `word` (`id`, `name`, `caption`, `rem`, `size`) VALUES
(1, NULL, '陂湖禀量', '比喻度量宽广恢弘。', 4),
(2, NULL, '北道主人', '北道上接待过客的主人。与“东道主人”同义。', 4),
(3, NULL, '备而不用', '准备好了，以备急用，眼下暂存不用。', 4),
(4, NULL, '贝阙珠宫', '用珍珠宝贝做的宫殿。形容房屋华丽。', 4),
(5, NULL, '笔诛墨伐', '笔、墨：指文字。诛：谴责。伐：声讨。通过文字加以谴责，进行声讨。', 4),
(6, NULL, '匕鬯不惊', '匕：古代的一种勺子；鬯：香酒；匕和鬯都是古代宗庙祭祀用物。形容军纪严明，所到之处，百姓安居，宗庙祭祀，照常进行。', 4),
(7, NULL, '毕恭毕敬', '形容态度十分恭敬。', 4),
(8, NULL, '婢作夫人', '婢：侍女；夫人：主妇。旧时指在文艺方面虽刻意模仿别人，但才力和作品的规模总赶不上。', 4),
(9, NULL, '壁垒森严', '壁垒：古代军营四周的围墙；森严：整齐，严肃。原指军事戒备严密。现也用来比喻彼此界限划得很分明。', 4),
(10, NULL, '鞭辟近里', '鞭辟：鞭策，激励；里：最里层。形容作学问切实。也形容分析透彻，切中要害。', 4),
(11, NULL, '抃风舞润', '抃：鼓掌；润：雨水。如燕在风中飞翔，象商羊在雨中起舞。原指同类的事物相互感应。后比喻意气相合。', 4),
(12, NULL, '便辞巧说', '指牵强附会、巧为立说。', 4),
(13, NULL, '彪炳千古', '形容伟大的业绩流传千秋万代。', 4),
(14, NULL, '别有肺肠', '别有：另有；肺肠：指思想。比喻人动机不良，故意提出一些与众不同的的奇特的主张。', 4),
(15, NULL, '秉笔直书', '秉：持，握住。写史书根据事实记录，不隐讳。', 4),
(16, NULL, '并蒂芙蓉', '蒂：花或瓜果跟枝茎相连的部分；芙蓉：荷花别名。两朵荷花并生一蒂。比喻夫妻相亲相爱。也比喻两者可以相媲美。', 4),
(17, NULL, '拨草寻蛇', '比喻招惹恶人，自找麻烦。', 4),
(18, NULL, '拨云见日', '拨开乌云见到太阳。比喻冲破黑暗见到光明。也比喻疑团消除，心里顿时明白。', 4),
(19, NULL, '伯道无儿', '伯道：邓伯道。旧时对他人无子的叹息。', 4),
(20, NULL, '卜夜卜昼', '卜：占卜。形容夜以继日地宴乐无度。', 4),
(21, NULL, '不茶不饭', '不思饮食。形容心事重重。', 4),
(22, NULL, '不齿于人', '齿：象牙齿一样排列。不能列于人类。指被人看不起。', 4),
(23, NULL, '不愤不启', '愤：心里想弄明白而还不明白。启：启发。指不到学生们想弄明白而还没有弄明白时，不去启发他。这是孔子的教学方法。', 4),
(24, NULL, '不名一钱', '名：占有。一个钱也没有。形容极其贫穷。', 4),
(25, NULL, '不期而然', '期：希望；然：如此。不希望这样，而竟然这样。表示出乎意外。', 4),
(26, NULL, '不期然而然', '没有想到是这样而竟然是这样。', 5),
(27, NULL, '不时之需', '不时：不是预定的时间。说不定什么时候会出现的需要。', 4),
(28, NULL, '不食之地', '不食：不生产粮食。指不能耕种的土地。', 4),
(29, NULL, '不为已甚', '已甚：过分。不做得太过分。多指对人的责备或处罚适可而止。', 4),
(30, NULL, '不文不武', '既不能文，又不能武。', 4),
(31, NULL, '不省人事', '省：知觉。指昏迷过去，失去知觉。也指不懂人情世故。', 4),
(32, NULL, '不一而足', '足：充足。指同类的事物不只一个而是很多，无法列举齐全。', 4),
(33, NULL, '不易一字', '不更动一个字。形容文章写得又快又好。', 4),
(34, NULL, '不义之财', '不义：不正当，不合理。不应该得到的或以不正当的手段获得的钱财。', 4),
(35, NULL, '不因人热', '因：依靠。汉时梁鸿不趁他人热灶烧火煮饭。比喻为人孤僻高傲。也比喻不依赖别人。', 4),
(36, NULL, '不知死活', '形容不知利害，冒昧从事。', 4),
(37, NULL, '布被瓦器', '布缝的被子，瓦制的器皿。形容生活检朴。', 4),
(38, NULL, '才德兼备', '才：才能。德：品德。备：具备。才能和品德都具备。', 4),
(39, NULL, '参透机关', '参：检验，察看。机关：阴谋或秘密所在。看透了阴谋和秘密。', 4),
(40, NULL, '仓皇出逃', '仓皇：匆忙，慌张。慌慌张张地外出逃跑。', 4),
(41, NULL, '草庐三顾', '顾：拜访。刘备为请诸葛亮，三次到草庐中去拜访他。后用此典故表示帝王对臣下的知遇之恩。也比喻诚心诚意地邀请或过访。', 4),
(42, NULL, '草长莺飞', '莺：黄鹂。形容江南暮春的景色。', 4),
(43, NULL, '侧目而视', '侧：斜着。斜着眼睛看人。形容憎恨或又怕又愤恨。', 4),
(44, NULL, '参差不齐', '参差：长短、高低不齐。形容水平不一或很不整齐。', 4),
(45, NULL, '层出不穷', '层：重复；穷：尽。接连不断地出现，没有穷尽。', 4),
(46, NULL, '插翅难飞', '插上翅膀也难飞走。比喻陷入困境，怎么也逃不了。', 4),
(47, NULL, '长安道上', '长安：古都名，在今陕西西安西北。旧喻名利场所。', 4),
(48, NULL, '长夜漫漫', '漫漫：无边际的样子。漫长的黑夜无边无际。多用来比喻社会的黑暗。', 4),
(49, NULL, '怅然若失', '怅然：形容不如意、不痛快。 象失去什么似的烦恼不快。', 4),
(50, NULL, '朝里无人莫做官', '旧时俗语。意思是，没有靠山，事办不成。', 7),
(51, NULL, '巢毁卵破', '鸟巢毁了，卵也一定会打碎。比喻大人遭难而牵连到子女。', 4),
(52, NULL, '沉博绝丽', '沉：深长；博：渊博。指文章的含义深远，内容渊博，文辞美妙。', 4),
(53, NULL, '沉疴难起', '沉疴：重病或老病。久患重病，不能下床。', 4),
(54, NULL, '臣门如市', '旧时形容居高位、掌大权的人宾客极多。', 4),
(55, NULL, '尘饭涂羹', '涂：泥。尘做的饭，泥做的羹。指儿童游戏。比喻没有用处的东西。', 4),
(56, NULL, '称家有无', '称：适合，相符。与家庭经济情况相符。指办理婚、丧等事不可过奢或过俭。', 4),
(57, NULL, '称王称霸', '王：帝王；霸：古代诸侯联盟的首领。比喻凭借权势横行一方，或狂妄地以首脑自居。', 4),
(58, NULL, '成败得失', '得：得利。失：失利。成功与失败，得到的与丢掉的。', 4),
(59, NULL, '成人不自在，自在不成', '人要有成就，必须刻苦努力，不可安逸自在。', 10),
(60, NULL, '成人之美', '成：成就。成全别人的好事。', 4),
(61, NULL, '成则为王，败则为贼', '旧指成功了就是合法的，失败了就是非法的。', 9),
(62, NULL, '诚惶诚恐', '诚：实在，的确；惶：害怕；恐：畏惧。非常小心谨慎以至达到害怕不安的程度。', 4),
(63, NULL, '乘车戴笠', '乘：坐，驾；笠：斗笠。比喻不因为富贵而改变贫贱之交。', 4),
(64, NULL, '承欢膝下', '承欢：旧指侍奉父母；膝下：子女幼时依于父母膝下，故表示幼年。旧指侍奉父母。', 4),
(65, NULL, '吃不了兜着走', '比喻受不了或担当不起。', 6),
(66, NULL, '池鱼笼鸟', '池里的鱼，笼里的鸟。比喻受束缚而失去自由的人。', 4),
(67, NULL, '尺壁寸阴', '阴：日影，光阴；寸阴：指极断的时间。日影移动一寸的时间价值比径尺的璧玉还要珍贵。极言时间可贵。', 4),
(68, NULL, '尺步绳趋', '绳、尺：木工校曲直、量长短的工具，引伸为法度；趋：快走；步：行走。指举动符合规矩，毫不随便。', 4),
(69, NULL, '叱咤风云', '叱咤：怒喝声。一声呼喊、怒喝，可以使风云翻腾起来。形容威力极大。', 4),
(70, NULL, '重睹天日', '比喻脱离黑暗，重见光明。', 4),
(71, NULL, '稠人广众', '稠：多而密。指人很多的场合。', 4),
(72, NULL, '仇人相见，分外眼红', '眼红：激怒的样子。仇敌碰在一起，彼此更加激怒。', 9),
(73, NULL, '绸缪束薪', '绸缪：紧紧缠绕。紧紧地把柴草捆扎成捆儿。古代束薪比喻婚姻的结合。', 4),
(74, NULL, '愁眉啼妆', '愁眉：使眉细而曲折；啼妆：轻轻地擦去目下的粉饰以作啼痕。形容妇女的妖态。', 4),
(75, NULL, '出尘不染', '比喻身处污浊的环境而能保持纯洁的节操。', 4),
(76, NULL, '刍荛之见', '刍荛：割草打柴的人。认为自己的意见很浅陋的谦虚说法。', 4),
(77, NULL, '杵臼之交', '杵：舂米的木棒；臼：石臼。比喻交朋友不计较贫富和身分。', 4),
(78, NULL, '穿凿附会', '穿凿：把讲不通的硬要讲通；附会：把不相干的事拉在一起。把讲不通的或不相干的道理、事情硬扯在一起进行解释。', 4),
(79, NULL, '疮痍满目', '疮痍：创伤。满眼创伤。比喻眼前看到的都是灾祸的景象。', 4),
(80, NULL, '床上安床', '床上安张床。比喻不必要的重复。', 4),
(81, NULL, '闯荡江湖', '指奔走四方，在外谋生。', 4),
(82, NULL, '吹吹打打', '指各种乐器的合奏。也用以形容故意渲染某种言行或事物，吸引别人注意。', 4),
(83, NULL, '吹灰之力', '比喻极轻微的力量。', 4),
(84, NULL, '吹弹得破', '好象吹一吹、弹一弹就会弄破似的。形容面部的皮肤非常细嫩。', 4),
(85, NULL, '垂范百世', '垂：流传。范：典范。指光辉榜样或伟大精神永远流传。', 4),
(86, NULL, '鹑居鷇食', '鷇：待母哺食的幼鸟，它不挑拣食物。如鹌鹑一样居无定所，象幼雏一样饥不择食。比喻生活俭朴，不求享受。', 4),
(87, NULL, '纯正无邪', '邪：不正当。纯洁真挚，没有一点邪念。', 4),
(88, NULL, '啜食吐哺', '辍：中止，停止。停止用饭，吐出口中含的食物。', 4),
(89, NULL, '词不达意', '词：言词；意：意思。指词句不能确切地表达出意思和感情。', 4),
(90, NULL, '从壁上观', '壁：壁垒。原指双方交战，自己站在壁垒上旁观。后多比喻站在一旁看着，不动手帮助。', 4),
(91, NULL, '粗衣淡饭', '粗：粗糙、简单；淡饭：指饭菜简单。形容饮食简单，生活简朴。', 4),
(92, NULL, '促膝谈心', '促：靠近；促膝：膝碰膝，坐得很近。形容亲密地谈心里话。', 4),
(93, NULL, '窜端匿迹', '比喻掩盖事情的真相。', 4),
(94, NULL, '村夫野老', '旧指生活在农村的农民和老人。', 4),
(95, NULL, '寸有所长', '寸比尺短，但用于更短处即显其长。比喻平平常常的人或事物，也会有他的长处。', 4),
(96, NULL, '搓手顿脚', '形容焦急不耐烦的样子。', 4),
(97, NULL, '大才槃槃', '槃槃：形容大的样子。指有大才干的人。', 4),
(98, NULL, '大公无私', '指办事公正，没有私心。现多指从集体利益出发，毫无个人打算。', 4),
(99, NULL, '大含细入', '原指文章的内容，既包涵天地的元气，又概括了极微小的事物。形容文章博大精深。', 4),
(100, NULL, '大好河山', '河山：国土。无限美好的祖国大地。', 4),
(101, NULL, '大声疾呼', '疾：快。大声呼喊，引起人们注意。', 4),
(102, NULL, '大相径庭', '径：小路；庭：院子；径庭：悬殊，偏激。比喻相差很远，大不相同。', 4),
(103, NULL, '大有可观', '指很值得看，很值得重视。', 4),
(104, NULL, '代拆代行', '拆：拆开，指拆阅来电来文；行：发出。一般指首长不在时由专人负责代理拆阅和审批、处理公文。形容权力极大。', 4),
(105, NULL, '担惊受怕', '形容十分担心或害怕。', 4),
(106, NULL, '丹凤朝阳', '比喻贤才逢明时。', 4),
(107, NULL, '胆大心粗', '做事胆子在，但心粗。考虑不周全。', 4),
(108, NULL, '惮赫千里', '惮赫：威震。威震千里。形容声威极盛。', 4),
(109, NULL, '当耳边风', '比喻对听到的话毫不在意。', 4),
(110, NULL, '岛瘦郊寒', '岛、郊：指唐代诗人贾岛和孟郊。贾岛、孟郊的诗中多凄苦哀婉之词，故以此指他们的诗歌和风格。亦形容与贾孟相类似诗文的风格与意', 4),
(111, NULL, '倒戈卸甲', '比喻放下武器认输。', 4),
(112, NULL, '悼心失图', '悼：悲伤；图：谋划。因悲痛而失去了主张。', 4),
(113, NULL, '倒背如流', '背：背诵。把书或文章倒过来背，背得像流水一样流畅。形容背得非常熟练，记得非常牢。', 4),
(114, NULL, '倒持泰阿', '泰阿：宝剑名。倒拿着剑，把剑柄给别人。比喻把大权交给别人，自己反受其害。', 4),
(115, NULL, '登木求鱼', '登：攀；木：树。爬到树上找鱼。比喻方向、方法不对，无法达到目的。', 4),
(116, NULL, '堤溃蚁孔', '堤坝因蚂蚁洞而崩溃。比喻忽视小处会酿成大祸。', 4),
(117, NULL, '羝羊触藩', '羝羊：公羊；触：抵撞；藩：篱笆。公羊的角缠在篱笆上，进退不得。比喻进退两难。', 4),
(118, NULL, '掂斤播两', '掂、播：托在掌上试轻重。比喻在小事情上过分计较。', 4),
(119, NULL, '点金乏术', '术：法术，方法。没有点铁成金的法术。比喻没有最好的方法筹集钱财，解决贫困。', 4),
(120, NULL, '电光朝露', '一闪而过的电光，日出以前的露水。比喻存在不久的事物。', 4),
(121, NULL, '刁斗森严', '刁斗：古时行军，在晚上敲击以报时或报警的用具。森严：整肃，整饬。防卫严密。形容军队的营地戒备森严。', 4),
(122, NULL, '吊尔郎当', '形容作风散漫，态度不严肃。', 4),
(123, NULL, '东拉西扯', '一会儿说东，一会儿说西。指说话条理斋乱，没有中心。', 4),
(124, NULL, '洞察其奸', '对别人的阴谋诡计看得很清楚。', 4),
(125, NULL, '冻解冰释', '如同冰冻融化一般。比喻困难或障碍消除。', 4),
(126, NULL, '斗粟尺布', '比喻兄弟间因利害冲突而不和。', 4),
(127, NULL, '斗折蛇行', '斗折：像北斗星的排列一样曲折。像北斗星一样弯曲，像蛇一样曲折行进。形容道路曲折蜿蜒。', 4),
(128, NULL, '都头异姓', '最高贵的称呼。', 4),
(129, NULL, '独步当时', '形容非常突出，一个时期内没有人能比得上。', 4),
(130, NULL, '独辟蹊径', '自己开辟一条路。比喻独创一种风格或新的方法。', 4),
(131, NULL, '独善其身', '独：唯独；善：好，维护。原意是做不上官就修养好自身。现指只顾自己，不管别人。', 4),
(132, NULL, '黩武穷兵', '黩：随便，任意；穷：竭尽。随意使用武力，不断发动侵略战争。形容极其好战。', 4),
(133, NULL, '妒能害贤', '贤、能：有德有才的人；害：陷害。嫉妒有才能的人，陷害贤明的人。指容不下才能、品德比自己高的人。', 4),
(134, NULL, '短褐穿结', '短褐：粗布短衣；穿：破；结：打结。形容衣衫褴褛。', 4),
(135, NULL, '断头将军', '比喻坚决抵抗，宁死不屈的将领。', 4),
(136, NULL, '度德量力', '度：估量；德：德行。衡量自己的德行是否能够服人，估计自己的能力是否能够胜任。', 4),
(137, NULL, '夺门而出', '夺门：破门，奋力冲开门。猛然奋力冲开门出去。形容迫不及待。', 4),
(138, NULL, '阿党相为', '阿党：偏袒、偏私一方。为了谋求私利相互偏袒、包庇。', 4),
(139, NULL, '蛾眉皓齿', '蛾眉：像蚕蛾触须似的弯而长的眉毛。修长的眉毛，洁白的牙齿。形容女子容貌美丽。', 4),
(140, NULL, '讹言惑众', '讹言：诈伪的话。用谣言欺骗迷惑群众。', 4),
(141, NULL, '恶迹昭著', '昭著：显著，明显。恶劣的事迹十分明显，人所共见。形容罪行严重。', 4),
(142, NULL, '扼吭夺食', '扼：用力掐着；亢：咽喉。扼住喉咙，夺走吃的东西。比喻使人处于绝境。', 4),
(143, NULL, '扼喉抚背', '抚：按，捺。掐住咽喉，按住脊背。比喻控制敌方的要害，使其丧失反抗的能力。', 4),
(144, NULL, '饿莩遍野', '莩：饿死的人。到处是饿死的人。形容人民因饥饿而大量死亡的悲惨景象。', 4),
(145, NULL, '尔虞我诈', '尔：你；虞、诈：欺骗。表示彼此互相欺骗。', 4),
(146, NULL, '发人深省', '发：启发；省：醒悟。启发人深刻思考，有所醒悟。', 4),
(147, NULL, '浮白载笔', '浮：罚人饮酒；白：指专用来罚酒的大杯；浮白：指喝酒和干杯；载笔：拿着笔。一面喝酒，一面写作。旧时比喻文人的雅量和才气。', 4),
(148, NULL, '伏低做小', '形容低声下气，巴结奉承。', 4),
(149, NULL, '芙蓉并蒂', '蒂：花或瓜果跟枝茎相连的部分；芙蓉：荷花别名。两朵荷花并生一蒂。比喻夫妻相亲相爱。也比喻两者可以相媲美。', 4),
(150, NULL, '拊背扼喉', '掐着喉咙，捺住脊背。比喻控制要害，制敌死命。', 4),
(151, NULL, '付之丙丁', '指用火烧掉。', 4),
(152, NULL, '父慈子孝', '父：指父母；子：子女。父母对子女慈爱，子女对父母孝顺。', 4),
(153, NULL, '蝮蛇螫手，壮士解腕', '手腕被腹蛇咬伤，便立即截断，以免毒液延及全身，危及生命。比喻事到紧要关头，必须下决心当机立断。也比喻牺牲局部，照顾全局。', 9),
(154, NULL, '肝肠寸断', '肝肠一寸寸断开。比喻伤心到极点。', 4),
(155, NULL, '甘败下风', '甘：情愿，乐意；下风：风向的下方。对人自认不如，真心佩服。', 4),
(156, NULL, '敢不承命', '敢：反语，“怎敢”、“不敢”之意；承：接受。怎么敢不承受你的命令呢？或怎么敢不接受你的意见呢？', 4),
(157, NULL, '干父之蛊', '干：承担，从事；蛊：事、事业。继承并能胜任父亲曾从事的事业。', 4),
(158, NULL, '刚愎自用', '愎：任性；刚愎：强硬回执；自用：自以为是。十分固执自信，不考虑别人的意见。', 4),
(159, NULL, '高材疾足', '高材：才能高；疾足：迈步快。形容人才能出众，行事敏捷。', 4),
(160, NULL, '高官显爵', '显：显要，显赫；爵：爵位，官爵。官职很高，爵们显赫。', 4),
(161, NULL, '隔年皇历', '皇历：原指清朝廷颁发的历书，后泛指历本。隔了一年的黄历。比喻过时的事物或陈旧的经验，在新的情况下已经用不上。', 4),
(162, NULL, '个中妙趣', '个中：其中；妙：美妙，奇妙；趣：情趣。其中的奥妙之处和情趣。', 4),
(163, NULL, '各人自扫门前雪，莫管', '莫管：不要管。比喻不要多管闲事。', 10),
(164, NULL, '更长梦短', '更：旧时夜间计时单位，一夜分五更，每更约两个小时；更长：指漫漫长夜；梦短：指睡眠不踏实，极易醒。形容思绪烦乱，很难入眠。', 4),
(165, NULL, '绠短汲深', '绠：汲水用的绳子；汲：从井里打水。吊桶的绳子短，打不了深井里的水。比喻能力薄弱，难以担任艰巨的任务。', 4),
(166, NULL, '工欲善其事，必先利其', '器：工具。要做好工作，先要使工具锋利。比喻要做好一件事，准备工作非常重要。', 10),
(167, NULL, '功败垂成', '垂：接近，快要。事情在将要成功的时候遭到了失败。', 4),
(168, NULL, '公报私仇', '假借公事报私人的仇恨。', 4),
(169, NULL, '公说公有理，婆说婆有', '比喻双方争执，各说自己有理。', 10),
(170, NULL, '弓调马服', '比喻办任何事情，应先做好准备工作。', 4),
(171, NULL, '勾魂摄魄', '形容事物具有非常大的吸引力或威吓的力量。', 4),
(172, NULL, '钩心斗角', '心：宫室中心；角：檐角。原指宫室建筑结构的交错和精巧。后比喻用尽心机，明争暗斗。', 4),
(173, NULL, '钩章棘句', '钩：弯曲；章：章节；棘：刺；句：文句。形容文辞艰涩拗口。', 4),
(174, NULL, '狗恶酒酸', '比喻环境险恶，使人裹足不前。', 4),
(175, NULL, '姑妄听之', '姑：暂且；妄：随便，胡乱。姑且随便听听，不一定就相信。', 4),
(176, NULL, '姑射神人', '姑射：山名；神人：得道的人。原指姑叶山的得到真人。后泛指美貌女子。', 4),
(177, NULL, '孤犊触乳', '原意是独生子因溺爱，助长了骄气，父母反受其害。后比喻无依无靠的人请求别人的援助。', 4),
(178, NULL, '孤立无援', '只有一个人或一方面的力量，得不到外力援助。', 4),
(179, NULL, '孤鸾寡鹄', '孤鸾：无偶的友鸾，比喻死去了配偶的男子；寡鹄：比喻寡妇。指失偶的男女。', 4),
(180, NULL, '古道热肠', '古道：上古时代的风俗习惯，形容厚道；热肠：热心肠。指待人真诚、热情。', 4),
(181, NULL, '股肱之力', '股肱：大腿和胳膊。自己的所有力量。形容做事已竭尽全力。', 4),
(182, NULL, '谷贱伤农', '谷：粮食。指粮价过低，使农民受到损害。', 4),
(183, NULL, '告朔饩羊', '原指鲁国自文公起不亲到祖庙告祭，只杀一只羊应付一下。后比喻照例应付，敷衍了事。', 4),
(184, NULL, '故步自封', '故：旧；故步：旧时行步之法，引伸为旧法；封：限制在一定的范围内。比喻守着老一套，不求进步。', 4),
(185, NULL, '固步自封', '比喻守着老一套，不求进步。', 4),
(186, NULL, '告贷无门', '告贷：向别人借钱。想借钱但没有地主借。指生活陷入困境。', 4),
(187, NULL, '瓜瓞绵绵', '瓞：小瓜；绵绵：延续不断的样子。如同一根连绵不断的藤上结了许多大大小小的瓜一样。引用为祝颂子孙昌盛。', 4),
(188, NULL, '瓜田不纳履，李下不整', '走过瓜田，不要弯下身子提鞋；经过李树下面，不要举起手来整理帽子。比喻避嫌疑。', 10),
(189, NULL, '关东出相，关西出将', '关：函谷关。函谷关以东的地区，民风好文，多出宰相；函谷关以西的地区，民风好武，多出将帅。', 9),
(190, NULL, '观过知仁', '察看一个人所犯过错的性质，就可以了解他的为人。', 4),
(191, NULL, '鳏寡孤独', '泛指没有劳动力而又没有亲属供养的人。', 4),
(192, NULL, '冠绝一时', '冠绝：遥遥领先，位居第一。形容在某一时期内超出同辈，首屈一指。', 4),
(193, NULL, '归根到底', '归结到根本上。', 4),
(194, NULL, '龟鹤遐寿', '遐：长久。祝人长寿的颂辞。', 4),
(195, NULL, '龟冷支床', '比喻壮志未酬，蛰居待时。', 4),
(196, NULL, '诡变多端', '诡变：狡诈多变；端：项目，点。形容坏主意很多。', 4),
(197, NULL, '佹得佹失', '佹：出于偶然的。指得失出于偶然。', 4),
(198, NULL, '贵耳贱目', '重视传来的话，轻视亲眼看到的现实。比喻相信传说，不重视事实。', 4),
(199, NULL, '海内存知己，天涯若比', '四海之内有知己朋友，即使远在天边，也感觉象邻居一样近。', 10),
(200, NULL, '骇浪惊涛', '骇：使惊怕；涛：大波浪。汹涌吓人的浪涛。比喻险恶的环境或尖锐激烈的斗争。', 4),
(201, NULL, '邯郸学步', '邯郸：战国时赵国的都城；学步：学习走路。比喻模仿人不到家，反把原来自己会的东西忘了。', 4),
(202, NULL, '含苞待放', '形容花朵将要开放时的形态。也比喻将成年的少女。', 4),
(203, NULL, '汉官威仪', '原指汉朝官吏的服饰制度。后常指汉族的统治制度。', 4),
(204, NULL, '行行出状元', '比喻不论干哪一行，只要热爱本职工作，都能做出优异的成绩。', 5),
(205, NULL, '豪夺巧取', '豪夺：强抢；巧取：软骗。旧时形容达官富豪谋取他人财物的手段。现指用各种方法谋取财物。', 4),
(206, NULL, '毫不讳言', '讳言：有顾虑，不愿把真实情况说出来。丝毫也没有隐讳不说的。', 4),
(207, NULL, '好事不出门，恶事行千', '指好事不容易被人知道，坏事却传播得极快。', 10),
(208, NULL, '好吃懒做', '好：喜欢、贪于。贪于吃喝，懒于做事。', 4),
(209, NULL, '好整以暇', '整：严整；暇：不急迫。形容既严整，又从容。也指事情虽多，仍旧从容不迫。', 4),
(210, NULL, '河东狮吼', '比喻悍妒的妻子对丈夫大吵大闹。', 4),
(211, NULL, '何患无辞', '何患：哪怕；辞：言辞。哪里用得着担心没有话说呢？常与“欲加之罪”连用，表示坏人诬陷好人时，无端捏造罪名，还说得振振有词。', 4),
(212, NULL, '合而为一', '把散乱的事物合在一起。', 4),
(213, NULL, '合从连衡', '从：通“纵”；衡：通“横”。指联合抗敌。', 4),
(214, NULL, '褐衣不完', '褐：粗布衣服。连粗布衣服也穿不周全。形容生活困苦。', 4),
(215, NULL, '横殃飞祸', '横：意外的。指意外的、平白无故的灾祸。', 4),
(216, NULL, '哄动一时', '在一个时期内惊动很多人。', 4),
(217, NULL, '轰动一时', '形容在一个时期里到处传播，影响很大。', 4),
(218, NULL, '弘毅宽厚', '弘毅：意志坚强，志向远大。志向远大而待人宽大厚道。', 4),
(219, NULL, '宏图大志', '宏伟的计划，远大的志向。', 4),
(220, NULL, '后车之戒', '对后面跟上来的车子的警戒。比喻以前的失败或挫折，可以作为以后做事情的借鉴或教训。', 4),
(221, NULL, '后患无穷', '以后的祸害没有个完。', 4),
(222, NULL, '囫囵半片', '囫囵：完整的，整个儿的。一个整体中的一部分。指只听懂或理解了其中的大概意思。', 4),
(223, NULL, '虎落平川', '平川：地势平坦的地方。老虎离开自己称王称霸的深山老林，落到了平地上。比喻有权有势或有实力者失去了自己的权势或优势。', 4),
(224, NULL, '花朝月夕', '有鲜花的早晨，有明月的夜晚。指美好的时光和景物。旧时也特指农历二月十五和八月十五。', 4),
(225, NULL, '华不再扬', '已开过的花，在一年里不会再开。比喻时间过去了不再回来。', 4),
(226, NULL, '华屋秋墟', '壮丽的建筑化为土丘。比喻兴亡盛衰的迅速。', 4),
(227, NULL, '华亭鹤唳', '华亭谷的鹤叫声。表示对过去生活的留恋。', 4),
(228, NULL, '画饼充饥', '画个饼来解除饥饿。比喻用空想来安慰自己。', 4),
(229, NULL, '划一不二', '指按照定价卖出，不打折扣。形容做事刻板。', 4),
(230, NULL, '怀宝迷邦', '怀宝：具有才德；迷邦：让国家迷乱。旧指有才德而不出来为国家效力。', 4),
(231, NULL, '涣然冰释', '涣然：流散的样子；释：消散。象冰遇热消融一般。形容疑虑、误会、隔阂等完全消除。', 4),
(232, NULL, '宦海风波', '宦海：旧指官场。旧指官场沉浮，像海洋中的浪涛和大风，变化莫测；亦指官场中出现的风险和波折。', 4),
(233, NULL, '皇皇不可终日', '惊慌地连一天都过不下去。形容惊恐不安到了极点。', 6),
(234, NULL, '灰飞烟灭', '比喻事物消失净尽。', 4),
(235, NULL, '毁于一旦', '于：在；一旦：一天。在一天的功夫全被毁掉。多指长期劳动的成果一下子被毁掉。', 4),
(236, NULL, '晦迹韬光', '晦、韬：隐藏；迹：踪迹；光：指才华。指将自己的才华隐藏起来，不使外露。', 4),
(237, NULL, '绘影绘声', '形容叙述或描写生动逼真。', 4),
(238, NULL, '荟萃一堂', '荟萃：草木繁茂，引申为杰出人物或精美东西的聚集；一堂：指在一个厅堂里。形容难逢的盛会。', 4),
(239, NULL, '浑身解数', '浑身：全身，指所有的；解数：那套数，指武艺。所有的本领，全部的权术手腕。', 4),
(240, NULL, '魂不附体', '附：依附。灵魂离开了身体。形容极端惊恐或在某种事物诱惑下失去常态。', 4),
(241, NULL, '混沌不分', '混沌：亦作“浑沌”，古代寓言中的中央帝名。原意是借喻一切事物都应顺其自然。后常用以表示愚昧无知。', 4),
(242, NULL, '混淆视听', '混淆：混杂。用假象或谎言让旁人分辨不清是非。', 4),
(243, NULL, '活灵活现', '形容神情逼真，使人感到好象亲眼看到一般。', 4),
(244, NULL, '豁达大度', '豁达：胸襟开阔；大度：试题大。形容人宽宏开通，能容人。', 4),
(245, NULL, '祸兮福所倚，福兮祸所', '倚：倚靠；伏：隐藏。祸中有福，福中有祸。比喻坏事可以引出好的结果，好事也可以引出坏的结果。', 10),
(246, NULL, '货真价实', '货物不是冒牌的，价钱也是实在的。形容实实在在，一点不假。', 4),
(247, NULL, '激昂慷慨', '精神振奋，情绪激昂，充满正气。', 4),
(248, NULL, '积案盈箱', '案：桌；盈：满。堆满书桌，塞满书箱。形容书籍、文稿、卷宗等非常多。', 4),
(249, NULL, '积财千万，不如薄技在', '积蓄财产，不如学点技术。', 10),
(250, NULL, '击鼓鸣金', '古时两军作战时用鼓和金发号施令，击鼓则进，鸣金则退。', 4),
(251, NULL, '击中要害', '要害：易于致命的部位，引伸为重要部门或问题的关键。正打中致命的部位。', 4),
(252, NULL, '赍粮藉寇', '赍：资助；藉：同“借”；寇：贼寇。借给敌寇兵器，送给盗贼粮食。比喻做有利于敌人、坏人的事。', 4),
(253, NULL, '机变如神', '机变：机智、权变。机智权变，神奇莫测。', 4),
(254, NULL, '饥不择食', '择：挑拣。不管什么都吃。比喻需要急迫，顾不得选择。', 4),
(255, NULL, '饥者易为食，渴者易为', '饥饿的人什么食物都可以吃，口渴的人什么饮品都可以喝。比喻需要急迫的人容易满足。', 10),
(256, NULL, '鸡犬之声相闻，老死不', '鸡鸣狗吠的声音都能听到，可是一辈子也不互相来往。现在形容彼此不了解，不互通音讯。', 10),
(257, NULL, '疾风劲草', '在猛烈的大风中，只有坚韧的草才不会被吹倒。比喻只有经过严峻的考验，才知道谁真正坚强。', 4),
(258, NULL, '及宾有鱼', '用别人的鱼请客。比喻借机培植私人势力。', 4),
(259, NULL, '极而言之', '从最极端的情况来说。指把话说到头。', 4),
(260, NULL, '极目迥望', '极：尽；迥：远。尽眼力之所及眺望远方。', 4),
(261, NULL, '岌岌不可终日', '形容情况非常危险，一天都过不下去。', 6),
(262, NULL, '急景凋年', '景：通“影”，光阴；凋：凋零。形容光阴迅速，一年将尽。也指年终的时候。', 4),
(263, NULL, '汲引忘疲', '汲引：从井里提水，比喻引荐和提拔人才。引荐和提拔人才而忘记了疲劳。', 4),
(264, NULL, '即以其人之道，还治其', '就用那个人对付别人的办法返回来对付那个人自己。', 10),
(265, NULL, '虮虱相吊', '虮：虱的幼卵；吊：慰问。虮和虱相互怜悯。比喻自怜即将灭亡。', 4),
(266, NULL, '掎裳连袂', '裙子牵住裙子，袖子连着袖子。形容人多。', 4),
(267, NULL, '几次三番', '番：次。一次又一次。形容次数之多。', 4),
(268, NULL, '济寒赈贫', '济：救济；赈：赈济。救助寒苦，赈济贫穷。', 4),
(269, NULL, '计不旋踵', '计：计议，打算；旋踵：旋转脚跟。脚跟还未转过来，计议就定了下来。形容在极短的时间内就拿定主意。也比喻行动迅速，毫不犹豫。', 4),
(270, NULL, '计出万全', '万全：非常安全周到。形容计划非常稳当周密，决不会发生意外。', 4),
(271, NULL, '稷蜂社鼠', '稷：五谷之神；社：土地庙。谷神庙里的马蜂，土地庙里的老鼠。比喻倚势作恶的人。', 4),
(272, NULL, '记问之学', '只是记诵书本，以资谈助或应答问难的学问。指对学问未融会贯通，不成体系。', 4),
(273, NULL, '浃髓沦肤', '浃：通，透；沦：陷入。浸透肌肉，深入骨髓。比喻感受极深。', 4),
(274, NULL, '家道从容', '指家庭生活条件充裕。', 4),
(275, NULL, '家给民足', '给：丰足，富裕。家家衣食充裕，人人生活富足。', 4),
(276, NULL, '夹袋人物', '旧指当权者的亲信或存记备用的人。', 4),
(277, NULL, '佳儿佳妇', '指好儿子，好媳妇。', 4),
(278, NULL, '加官进爵', '爵：爵位，君主国家封贵族的等级。旧时指官职提升。', 4),
(279, NULL, '夹板医驼子', '比喻只顾这一方面，不顾那一方面。', 5),
(280, NULL, '戛戛独造', '戛戛：形容独创。别出心裁，富有创造性。', 4),
(281, NULL, '甲第连天', '甲第：富豪显贵的宅第。形容富豪显贵的住宅非常之多。', 4),
(282, NULL, '嫁祸于人', '嫁：转移。把自己的祸事推给别人。', 4),
(283, NULL, '价廉物美', '廉：便宜。东西价钱便宜，质量又好。', 4),
(284, NULL, '驾轻就熟', '驾：赶马车。驾轻车，走熟路。比喻对某事有经验，很熟悉，做起来容易。', 4),
(285, NULL, '驾雾腾云', '乘着云，驾着雾。原是传说中指会法术的人乘云雾飞行，后形容奔驰迅速或头脑发昏。', 4),
(286, NULL, '肩摩毂击', '肩膀和肩膀相摩，车轮和车轮相撞。形容行人车辆往来拥挤。', 4),
(287, NULL, '监守自盗', '监守：监管；盗：偷窃。窃取公务上自己看管的财物。', 4),
(288, NULL, '尖担两头脱', '两头尖的扁担无法挑东西。比喻两头落空。', 5),
(289, NULL, '奸同鬼蜮，行若狐鼠', '奸诈象鬼蜮，狡猾象狐鼠。比喻人恶劣到极点。', 9),
(290, NULL, '茧丝牛毛', '形容功夫细密。', 4),
(291, NULL, '间不容发', '间：空隙。空隙中容不下一根头发。比喻与灾祸相距极近或情势危急到极点。', 4),
(292, NULL, '见鞍思马', '看见死去或离别的人留下的东西就想起了这个人。', 4),
(293, NULL, '见风是雨', '比喻只看到一点迹象就轻率地相信是真的。', 4),
(294, NULL, '见机而行', '见机：看时机，看情况。看具体情况灵活办事。', 4),
(295, NULL, '僭赏滥刑', '赏的不是有功的人，罚的不是有罪的人。泛指赏罚不当。', 4),
(296, NULL, '姜桂之性', '生姜和肉桂愈久愈辣。比喻年纪越大性格越耿直。', 4),
(297, NULL, '江东父老', '江东：古指长江以南芜湖以下地区；父老：父兄辈人。泛指家乡的父兄长辈。', 4),
(298, NULL, '将门虎子', '将门：世代为将的人家。比喻父辈有才能，子孙也身手不凡。也指后生子弟不辱门庭。', 4),
(299, NULL, '胶柱调瑟', '瑟：古代一种弹拨乐器。用胶粘住瑟上用以调音的短木，不能再调整音的高低缓急。比喻拘泥死板，缺少变通。', 4),
(300, NULL, '郊寒岛瘦', '本指孟郊、贾岛简啬孤峭的诗歌风格。后用以形容诗文类似的意境。', 4),
(301, NULL, '叫苦不迭', '不迭：不停止。形容连声叫苦。', 4),
(302, NULL, '接二连三', '一个接着一个，接连不断。', 4),
(303, NULL, '皆大欢喜', '皆：都。人人都高兴满意。', 4),
(304, NULL, '劫富济贫', '劫：强取；济：救济。夺取富人的财产，救济穷人。', 4),
(305, NULL, '节哀顺变', '节：节制；变：事变。抑制哀伤，顺应变故。用来慰唁死者家属的话。', 4),
(306, NULL, '竭泽而渔', '泽：池、湖。掏干了水塘捉鱼。比喻取之不留余地，只图眼前利益，不作长远打算。也形容反动派对人民的残酷剥削。', 4),
(307, NULL, '桀骜不驯', '桀：凶暴；骜：马不驯良，比喻傲慢。性情强暴不驯顺。', 4),
(308, NULL, '孑然一身', '孑：单独。孤孤单单一个人。', 4),
(309, NULL, '今不如昔', '昔：往昔，过去。现在不如过去。多用于表示对世事的不满情绪。', 4),
(310, NULL, '今是昨非', '现在是对的，过去是错的。指认识过去的错误。', 4),
(311, NULL, '巾帼英雄', '巾帼：古代妇女配戴的头巾和发饰，后借指妇女。指女子中的英雄。', 4),
(312, NULL, '金榜题名', '金榜：科举时代称殿试揭晓的榜；题名：写上名字。指科举得中。', 4),
(313, NULL, '金壶墨汁', '指极其珍贵罕见的书画用品。', 4),
(314, NULL, '斤斤计较', '斤斤：形容明察，引伸为琐碎细小。只对无关紧要的事过分计较。', 4),
(315, NULL, '紧打慢敲', '比喻用各种办法催迫。', 4),
(316, NULL, '锦瑟年华', '比喻青春时代。', 4),
(317, NULL, '仅容旋马', '指住宅地方狭小。', 4),
(318, NULL, '进旅退旅', '旅：共，同。与众人一起进退。形容跟着大家走，自己没有什么主张。', 4),
(319, NULL, '进身之阶', '进身：上升；阶：台阶。使身体能够上升的阶梯。旧指借以提拔升迁的门路。', 4),
(320, NULL, '进退无据', '前进和后退都失去了依据。形容无处容身。也指进退两难。', 4),
(321, NULL, '近火先焦', '比喻跟祸事最接近的人首先遭殃。', 4),
(322, NULL, '列鼎而食', '列：陈列；食：吃。形容豪门贵族的奢侈生活。', 4),
(323, NULL, '鳞次栉比', '栉：梳篦的总称。象鱼鳞和梳子齿那样有次序地排列着。多用来形容房屋或船只等排列得很密很整齐。', 4),
(324, NULL, '临别赠言', '分别时赠送的一些勉励的话。', 4),
(325, NULL, '陵谷变迁', '陵：大土山；谷：两山之间的夹道。丘陵变山谷，山谷变丘陵。比喻世事变迁，高下易位。', 4),
(326, NULL, '玲珑剔透', '玲珑：精巧细致；剔透：孔穴明晰。形容器物精致通明，结构细巧。也比喻人精明灵活。', 4),
(327, NULL, '令不虚行', '指制定的法令必须切实执行。', 4),
(328, NULL, '留得青山在，不愁没柴', '比喻只要基础或根本还存在，暂时遭受损失或挫折无伤大体。', 10),
(329, NULL, '柳树上着刀，桑树上出', '比喻代人受过。', 10),
(330, NULL, '六出奇计', '原指陈平所出的六条妙计。后泛指出奇制胜的谋略。', 4),
(331, NULL, '路遥知马力，日久见人', '路途遥远才能知道马的力气大小，日子长了才能看出人心的好坏。', 10),
(332, NULL, '陆海潘江', '陆：晋朝陆机；潘：晋朝潘岳。陆机的文才如大海，潘岳的文才如长江。比喻学识渊博，才华横溢的人。', 4),
(333, NULL, '驴唇不对马嘴', '比喻答非所问或两下不相合。', 6),
(334, NULL, '乱弹琴', '比喻胡扯或胡闹。', 3),
(335, NULL, '洛阳纸贵', '比喻著作有价值，流传广。', 4),
(336, NULL, '络绎不绝', '形容行人车马来来往往，接连不断。', 4),
(337, NULL, '慢藏诲盗', '慢藏：收藏不慎；诲：教导。收藏财物不慎，等于诱人偷窃。', 4),
(338, NULL, '盲人把烛', '瞎子手擎烛火照亮。比喻不起任何作用。', 4),
(339, NULL, '每饭不忘', '指时刻不忘。', 4),
(340, NULL, '蒙混过关', '用欺骗的手段逃避询问或审查。', 4),
(341, NULL, '蒙昧无知', '蒙昧：知识未开。没有知识，不明事理。指糊涂不懂事理。', 4),
(342, NULL, '猛志常在', '比喻雄心壮志，至死不变。', 4),
(343, NULL, '迷而不返', '迷失了道路，不知道回来。比喻犯了错误不知道改正。', 4),
(344, NULL, '弥缝其阙', '弥缝：补救；阙：过错。补救行事的过失。', 4),
(345, NULL, '密不通风', '密：严密。形容包围紧密或防卫严密，连风也透不进去。', 4),
(346, NULL, '绵里薄材', '力量小，没有什么才能。常用作谦词。', 4),
(347, NULL, '免开尊口', '免：不要。不必开口说什么。多表示要求不会得到同意。', 4),
(348, NULL, '偭规越矩', '偭：违背；越：逾越；规、矩：一定的标准、法则和习惯。违反正常的法则。', 4),
(349, NULL, '民安物阜', '阜：多。人民平安，物产丰富。形容社会安定，经济繁荣的景象。', 4),
(350, NULL, '名标青史', '标：写明；青史：古代在竹简上记事，因称史书。把姓名事迹记载在历史书籍上。形容功业巨大，永垂不朽。', 4),
(351, NULL, '明察秋毫之末，而不见', '眼力能看到一根毫毛的末梢，而看不到一车柴草。比喻只看到小处，看不到大处。', 10),
(352, NULL, '摩顶放踵', '从头顶到脚跟都擦伤了。形容不辞劳苦，不顾身体。', 4),
(353, NULL, '摩肩击毂', '肩膀和肩膀相摩，车轮和车轮相撞。形容行人车辆往来拥挤。', 4),
(354, NULL, '模棱两可', '模棱：含糊，不明确；两可：可以这样，也可以那样。指不表示明确的态度，或没有明确的主张。', 4),
(355, NULL, '莫名其妙', '说不出其中的奥妙。指事情很奇怪，说不出道理来。', 4),
(356, NULL, '莫予毒也', '再也没有人威胁、危害我了。表示目空一切，认为谁也不能伤害我。', 4),
(357, NULL, '脉脉含情', '脉脉：两眼凝神，要向别人诉说心曲的样子。饱含温情，默默地用眼神表达自己的感情。形容用眼神或行动默然地表达情意。', 4),
(358, NULL, '侔色揣称', '侔：相等；揣：估量；称：好。形容描写景物，恰到好处。', 4),
(359, NULL, '牡丹虽好，全仗绿叶扶', '比喻人不管有多大能耐，总得有人在旁协助。', 10),
(360, NULL, '幕天席地', '把天作幕，把地当席。原形容心胸开阔。现形容在野外作业的艰苦生活。', 4),
(361, NULL, '沐猴而冠', '沐猴：猕猴；冠：戴帽子。猴子穿衣戴帽，究竟不是真人。比喻虚有其表，形同傀儡。常用来讽刺投靠恶势力窃据权位的人。', 4),
(362, NULL, '拿刀动杖', '挥舞刀枪棍棒。泛指动武。', 4),
(363, NULL, '难分难解', '指双方争吵、斗争、比赛等相持不下，难以分开。有时也形容双方关系十分亲密，分不开。', 4),
(364, NULL, '难兄难弟', '指共过患难的人或彼此处于同样困境的人。', 4),
(365, NULL, '内顾之忧', '内顾：在外边而顾念家事或国事。旧时形容没有妻子，身在外又要顾虑家事。现形容有内部的忧虑。', 4),
(366, NULL, '内省不疚', '指自我反省，内心并不感到惭愧不安，即没有做有愧于心的事。', 4),
(367, NULL, '呢喃细语', '形容小声说话。', 4),
(368, NULL, '你死我活', '不是你死，就是我活。形容双方斗争非常激烈。', 4),
(369, NULL, '匿迹销声', '指隐藏进来或不公开露面。', 4),
(370, NULL, '年富力强', '年富：未来的年岁多。形容年纪轻，精力旺盛。', 4),
(371, NULL, '涅而不缁', '涅：矿物名，古代用作黑色染料；缁：黑色。用涅染也染不黑。比喻品格高尚，不受恶劣环境的影响。', 4),
(372, NULL, '宁缺毋滥', '宁：宁愿；毋：不；滥：过度。选拔人才或挑选事物，宁可少一些，也不要不顾质量贪多凑数。', 4),
(373, NULL, '怒从心头起，恶向胆边', '比喻愤怒到极点就会胆大得什么事都干得出来。也泛指恼怒到极点。', 10),
(374, NULL, '女长须嫁', '女子成年后必须出嫁。', 4),
(375, NULL, '偶一为之', '偶：偶尔；为：做。指平常很少这样做，偶尔才做一次。', 4),
(376, NULL, '徘徊歧路', '徘徊：在一个地方来回地走，比喻犹豫；歧路：岔路。在岔道上走来走去。比喻犹豫观望，主意不定。', 4),
(377, NULL, '旁见侧出', '从不同的角度和侧面表现的形象。', 4),
(378, NULL, '庞眉皓发', '庞：杂色；皓：洁白。眉发花白。形容老人相貌。', 4),
(379, NULL, '咆哮如雷', '形容人暴怒喊叫的神态。', 4),
(380, NULL, '炮火连天', '形容炮火非常猛烈。', 4),
(381, NULL, '朋比为奸', '朋比：依附，互相勾结。坏人勾结在一起干坏事。', 4),
(382, NULL, '批红判白', '批：削；判：分开；红、白：指花卉。嫁接花木。', 4),
(383, NULL, '被发文身', '被发：散发；文身：身上刺花纹。原指古代吴越一代的风俗。后也用以泛指未开化地带的风俗。', 4),
(384, NULL, '枇杷门巷', '旧时指妓女居住的地方。', 4),
(385, NULL, '皮开肉绽', '绽：裂开。皮肉都裂开了。形容伤势严重。多指受残酷拷打。', 4),
(386, NULL, '否极泰来', '否、泰：《周易》中的两个卦名。否：卦不顺利；泰：卦顺利；极：尽头。逆境达到极点，就会向顺境转化。指坏运到了头好运就来了。', 4),
(387, NULL, '屁滚尿流', '形容惊慌或欣喜到极点。', 4),
(388, NULL, '骈拇枝指', '骈母：脚上的拇指与第二趾合成一趾；枝指：手上大拇指帝多生一指。比喻多余的、无用的东西。', 4),
(389, NULL, '片甲不存', '一片铠甲都没保存下来。形容全军覆没。', 4),
(390, NULL, '漂泊无定', '飘泊：随水漂流或停留。随水飘流到哪里就停在哪里，没有一定的方向。比喻为了生活而四处奔走，生活不安定。', 4),
(391, NULL, '平时不烧香，急来抱佛', '谚语。原比喻平时不往来，遇有急难才去恳求。后多指平时没有准备，临时慌忙应付。', 10),
(392, NULL, '凭几据杖', '形容傲慢不以礼待客。', 4),
(393, NULL, '迫不得已', '被逼得没有办法，不得不这样。', 4),
(394, NULL, '仆仆风尘', '仆仆：行路劳累的样子；风尘：指行旅，含有辛苦之意。形容奔波忙碌，旅途劳累。', 4),
(395, NULL, '暴腮龙门', '象鱼仰望龙门而不得上一样。科举时代比喻应进士试不第，后也比喻生活遭遇挫折，处境窘迫。', 4),
(396, NULL, '七步之才', '形容才思敏捷。', 4),
(397, NULL, '七横八竖', '有的横，有的竖，杂乱无章。形容纵横杂乱。', 4),
(398, NULL, '其味无穷', '其中的味道没有穷尽。形容含义深刻，使人回味不尽。', 4),
(399, NULL, '棋逢对手', '逢：相遇。下棋遇到对手。比喻争斗的双方本领不相上下。', 4),
(400, NULL, '骐骥过隙', '形容时间过得飞快。', 4),
(401, NULL, '奇才异能', '奇：少见的；异：特别的。奇异杰出的才能。', 4),
(402, NULL, '杞人忧天', '杞：周代诸侯国名，在今河南杞县一带。杞国有个人怕天塌下来。比喻不必要的或缺乏根据的忧虑和担心。', 4),
(403, NULL, '岂有此理', '哪有这个道理。指别人的言行或某一事物极其荒谬。', 4),
(404, NULL, '弃暗投明', '离开黑暗，投向光明。比喻在政治上脱离反动阵营，投向进步方面。', 4),
(405, NULL, '弃书捐剑', '比喻胸怀大大志的人不屑于小事。', 4),
(406, NULL, '气冲牛斗', '气：气势；牛、斗：即牵牛星和北斗星，指天空。形容怒气冲天或气势很盛。', 4),
(407, NULL, '铅刀一割', '铅刀虽不锋利，偶尔用得得当，也能割断东西。比喻才能平常的人有时也能有点用处。多作请求任用的谦词。', 4),
(408, NULL, '牵肠挂肚', '牵：拉。形容十分惦念，放心不下。', 4),
(409, NULL, '千变万化', '形容变化极多。', 4),
(410, NULL, '千里不同风，百里不同', '指各地各有各的风俗习惯。', 10),
(411, NULL, '千言万语', '形容说得话很多。', 4),
(412, NULL, '前不巴村，后不巴店', '指走远道处在无处落脚的境地。也比喻处境尴尬或生活无依靠。', 9),
(413, NULL, '前赴后继', '前面的冲上去了，后面的紧跟上来。形容不断投入战斗，奋勇冲杀向前。', 4),
(414, NULL, '钱可通神', '有了钱连鬼神也可以买通。比喻金钱的魔力极大。', 4),
(415, NULL, '将伯之呼', '指求人帮助。', 4),
(416, NULL, '墙倒众人推', '旧时比喻在一个人受挫折的时候，大家乘机打击他。', 5),
(417, NULL, '强本节用', '本：我国古代以农为本。加强农业生产，节约费用。', 4),
(418, NULL, '桥归桥，路归路', '比喻互不相干的事应该严格区分开来。', 7),
(419, NULL, '乔龙画虎', '形容假心假意地献殷勤。', 4),
(420, NULL, '悄然无声', '悄然：寂静无声的样子。静悄悄的，听不到一点声音。', 4),
(421, NULL, '怯防勇战', '小心设防，勇敢出战。', 4),
(422, NULL, '切齿腐心', '切齿：咬紧牙齿；腐心：形容心中极恨。形容愤恨到极点。', 4),
(423, NULL, '钦差大臣', '过去指由皇帝派遣并代表皇帝外出办理重大事件的官员。现在用来讽刺从上级机关派到下面去，不了解情况就指手画脚发号施令的人。', 4),
(424, NULL, '禽困覆车', '禽兽被围捕急了，也会把人的车子撞翻。比喻人在走投无路时就会冒险。', 4),
(425, NULL, '琴断朱弦', '比喻妇女死掉丈夫。', 4),
(426, NULL, '琴瑟调和', '琴瑟同时弹奏，声音和谐。比喻夫妻恩爱。', 4),
(427, NULL, '清夜扪心', '清：清静；扪：按摸。指深夜不眠，进行反省。', 4),
(428, NULL, '倾肠倒肚', '比喻把心里的话全都讲出来。', 4),
(429, NULL, '倾耳而听', '倾：歪，侧着。侧着耳朵仔细听。', 4),
(430, NULL, '轻才好施', '指人不把钱财放在眼里，乐于慷慨解囊，急公好义。', 4),
(431, NULL, '轻举妄动', '轻：轻率；妄：任意。指不经慎重考虑，轻率地采取行动。', 4),
(432, NULL, '轻如鸿毛', '鸿毛：大雁的毛。比大雁的毛还轻。比喻毫无价值。', 4),
(433, NULL, '情见乎辞', '见：通“现”；乎：于。情感表现在言辞当中。', 4),
(434, NULL, '穷源竟委', '穷、竟：彻底推求；源：水流的源头；委：水的下流。比喻彻底搞清楚事情的始末。', 4),
(435, NULL, '茕茕孑立，形影相吊', '茕茕：孤独的样子；孑：孤单；形：指身体；吊：慰问。孤身一人，只有和自己的身影相互慰问。形容无依无靠，非常孤单。', 9),
(436, NULL, '秋月春风', '指良辰美景。也指美好的岁月。', 4),
(437, NULL, '丘山之功', '比喻功绩伟大。', 4),
(438, NULL, '囚首垢面', '象监狱里的犯人，好久没有理发和洗脸。形容不注意清洁、修饰。', 4),
(439, NULL, '区区小事', '区区：小，少。形容很小很少的小事。', 4);
INSERT INTO `word` (`id`, `name`, `caption`, `rem`, `size`) VALUES
(440, NULL, '曲高和寡', '曲调高深，能跟着唱的人就少。旧指知音难得。现比喻言论或作品不通俗，能了解的人很少。', 4),
(441, NULL, '拳头上立得人，胳膊上', '比喻为人清白，作风正派，过得硬。', 10),
(442, NULL, '权衡轻重', '权衡：衡量。衡量哪个轻，哪个重。比喻比较利害得失的大小。', 4),
(443, NULL, '全功尽弃', '功：功效，功绩；弃：丢掉。全部功效都丧失干净。', 4),
(444, NULL, '全军覆没', '整个军队全部被消灭。比喻事情彻底失败。', 4),
(445, NULL, '犬牙交错', '错：交叉，错杂。比喻交界线很曲折，象狗牙那样参差不齐。也比喻情况复杂，双方有多种因素参差交错。', 4),
(446, NULL, '雀角鼠牙', '雀、鼠：比喻强暴者。原意是因为强暴者的欺凌而引起争讼。后比喻打官司的事。', 4),
(447, NULL, '裙带关系', '裙带：比喻妻女、姊妹的亲属。指相互勾结攀援的妇女姻亲关系。', 4),
(448, NULL, '热肠古道', '热肠：热心肠；古道：上古时代的风俗习惯，形容厚道。指待人真诚、热情。', 4),
(449, NULL, '人给家足', '给：富裕，充足。家家户户丰衣足食。', 4),
(450, NULL, '人迹罕至', '罕：少。人很少到的地方。指偏僻荒凉的地方很少有人来过。', 4),
(451, NULL, '人生何处不相逢', '指人与人分手后总是有机会再见面的。', 7),
(452, NULL, '人生如朝露', '朝露：早晨的露水，比喻存在的时间短。比喻人生短促。', 5),
(453, NULL, '人为财死，鸟为食亡', '旧时俗语。意思是为了追求金钱，连生命都可以不要。', 9),
(454, NULL, '人心归向', '向：归向，向往。指人民群众所拥护的，向往的。', 4),
(455, NULL, '日积月累', '一天一天地、一月一月地不断积累。指长时间不断地积累。', 4),
(456, NULL, '日久天长', '时间长，日子久。', 4),
(457, NULL, '日出三竿', '太阳升起有三根竹竿那样高。形容太阳升得很高，时间不早了。也形容人起床太晚。', 4),
(458, NULL, '日省月试', '省：检查；试：考核。每天检查，每月考核。形容经常查考。', 4),
(459, NULL, '日月蹉跎', '蹉跎：时光白白过去。把时光白白地耽误过去。指虚度光阴。', 4),
(460, NULL, '日月参辰', '参辰：参宿和心宿，这两星宿不同在天空中出现。比喻不合，作对。', 4),
(461, NULL, '戎马倥偬', '戎马：本指战马，借指军事；倥偬：繁忙。形容军务繁忙。', 4),
(462, NULL, '如臂使指', '像胳膊支配手指那样。比喻指挥如意，没有牵制。', 4),
(463, NULL, '如影随形', '好象影子总是跟着身体一样。比喻两个人关系亲密，常在一起。', 4),
(464, NULL, '乳臭未干', '臭：气味。身上的奶腥气还没有退尽。对年轻人表示轻蔑的说法。', 4),
(465, NULL, '入木三分', '相传王羲之在木板上写字，木工刻时，发现字迹透入木板三分深。形容书法极有笔力。现多比喻分析问题很深刻。', 4),
(466, NULL, '若要人不知，除非己莫', '要想人家不知道，除非自己不去做。指干了坏事终究要暴露。', 10),
(467, NULL, '三茶六饭', '比喻招待客人非常周到。', 4),
(468, NULL, '三个臭皮匠，赛过诸葛', '比喻人多智慧多，有事请经过大家商量，就能商量出一个好办法来。', 10),
(469, NULL, '三生有幸', '三生：佛家指前生、今生、来生；幸：幸运。三世都很幸运。比喻非常幸运。', 4),
(470, NULL, '三十年河东，三十年河', '三十年前风水在河的东面，而三十年后却在河的西面。比喻世事变化，盛衰无常。', 10),
(471, NULL, '三省吾身', '省：检查、反省；身：自身。原指每日从三个方面检查自己，后指多次自觉地检查自己。', 4),
(472, NULL, '三旨相公', '用来讽刺庸禄低能的大官。', 4),
(473, NULL, '色厉内荏', '色：神色，样子；厉：凶猛；荏：软弱。外表强更，内心虚弱。', 4),
(474, NULL, '杀敌致果', '致：使得到；果：果敢除敌。勇敢杀敌，建立战功。', 4),
(475, NULL, '杀鸡骇猴', '传说猴子怕见血，驯猴的人便杀鸡放血来恐吓猴子。比喻用惩罚一个人的办法来警告别的人。', 4),
(476, NULL, '煽风点火', '比喻煽动别人闹事。', 4),
(477, NULL, '山肴野蔌', '肴：熟的鱼肉；簌：野菜。指山中的野味和野菜。', 4),
(478, NULL, '山陬海噬', '形容遥远偏僻的地方。', 5),
(479, NULL, '芟繁就简', '芟：除去；就：趋向。去掉繁杂部分，使它趋于简明。', 4),
(480, NULL, '删繁就简', '删：除去；就：趋向。去掉繁杂部分，使它趋于简明。', 4),
(481, NULL, '潸然泪下', '潸然：流泪的样子。形容眼泪流下来。', 4),
(482, NULL, '善罢干休', '善：好好地；甘休：情愿罢休。好好地解决纠纷，不再闹下去（多用于否定）。', 4),
(483, NULL, '上无片瓦，下无插针之', '头顶上没有一片瓦，肢底下没有插针的地方。形容一无所有，贫困到了极点。', 10),
(484, NULL, '上无片瓦，下无立锥之', '形容一无所有，贫困到了极点。', 10),
(485, NULL, '上烝下报', '烝：晚辈男子和长辈女子通奸；报：长辈男子与晚辈女子通奸。泛指男女乱伦。', 4),
(486, NULL, '少不更事', '少：年轻；更：经历。年纪轻，没有经历过什么事情。指经验不多。', 4),
(487, NULL, '少壮不努力，老大徒伤', '年轻力壮的时候不奋发图强，到了老年，悲伤也没用了。', 10),
(488, NULL, '舌敝唇焦', '敝：破碎；焦：干枯。说话说得舌头都破了，嘴唇都干了。形容费尽了唇舌。', 4),
(489, NULL, '舍得一身剐，敢把皇帝', '比喻再难的事，拼着一死也敢干下去。', 10),
(490, NULL, '舍己救人', '舍己：牺牲自己。不惜牺牲自己去救别人。', 4),
(491, NULL, '设身处地', '设：设想。设想自己处在别人的那种境地。指替别人的处境着想。', 4),
(492, NULL, '社威擅势', '倚仗权势，想干什么就干什么。', 4),
(493, NULL, '申冤吐气', '洗雪冤屈，发泄怨恨。', 4),
(494, NULL, '参辰卯酉', '参星酉时出于西方，辰星卯时出于东方。参辰二星，各出于东西，永不相合，用以比喻敌对者势不两立，或比喻双方分离，不得相会。', 4),
(495, NULL, '深恶痛疾', '恶：厌恶；痛：痛恨。指对某人或某事物极端厌恶痛恨。', 4),
(496, NULL, '神圣工巧', '中医对望、闻、问、切四种方法的别称。', 4),
(497, NULL, '生死相依', '在生死问题上互相依靠。形容同命运，共存亡。', 4),
(498, NULL, '生死予夺', '生：让人活；死：处死；予：给予；夺：剥夺。形容反动统治者掌握生死、赏罚大权。', 4),
(499, NULL, '升山采珠', '到山上去采珍珠。比喻办事的方向、方法错误，一定达不到目的。', 4),
(500, NULL, '胜败乃兵家常事', '胜利或失败是带兵作战的人常遇到的事情。意思是不要把偶然一次的胜利或失败看得太重。', 7),
(501, NULL, '胜残去杀', '感化残暴的人使其不再作恶，便可废除死刑。也指以德化民，太平至治。', 4),
(502, NULL, '诗肠鼓吹', '鼓吹：乐器合奏。特指听到黄鹂鸣声，可以引起诗兴。', 4),
(503, NULL, '师出无名', '师：军队；名：名义，引伸为理由。出兵没有正当理由。也引申为做某事没有正当理由。', 4),
(504, NULL, '失败为成功之母', '失败往往是成功的先导。指从失败中吸取教训，最后取得胜利。', 7),
(505, NULL, '尸横遍野', '尸体到处横着。形容死者极多。', 4),
(506, NULL, '识二五而不知十', '只知道一个方面，而不懂得全面地观察问题。', 7),
(507, NULL, '十病九痛', '形容浑身病痛。', 4),
(508, NULL, '十里长亭', '秦汉时每十里设置一亭，以后每五里有一短亭，供行人何处，亲友远行常在此话别。', 4),
(509, NULL, '时不可失', '时：时机，机会；失：错过。抓住时机，不可错过。', 4),
(510, NULL, '食不甘味', '甘味：感到味道好。吃东西都觉得没有味道。形容心里有事，吃东西也不香。', 4),
(511, NULL, '史不绝书', '书：指记载。史书上不断有记载。过去经常发生这样的事情。', 4),
(512, NULL, '矢口抵赖', '一口咬定，死不承认。', 4),
(513, NULL, '市井之臣', '市井：古时称做买卖的地方。旧指城市里的老百姓。', 4),
(514, NULL, '世道人心', '社会的风气，人们的思想。', 4),
(515, NULL, '世上无难事，只怕有心', '指只要肯下决心去做，任何困难都能克服。', 10),
(516, NULL, '天下本无事，庸人自扰', '指本来没有事，自己瞎着急或自找麻烦。', 10),
(517, NULL, '天下无难事，只怕有心', '指只要有志向，有毅力，没有什么办不到的事情。', 10),
(518, NULL, '天有不测风云，人有旦', '不测：料想不到。比喻有些灾祸的发生，事先是无法预料的。', 10),
(519, NULL, '田父献曝', '田父：老农；曝：晒。老农将晒太阳取暖的方法献给国君。常作向人献物或献计的谦词。', 4),
(520, NULL, '调和鼎鼐', '鼎：古代烹调食物的器具，三足两耳；鼐：大鼎。于鼎鼐中调味。比喻处理国家大事。多指宰相职责。', 4),
(521, NULL, '挑拨离间', '搬弄是非，使别人不团结。', 4),
(522, NULL, '粜风卖雨', '比喻招摇撞骗。', 4),
(523, NULL, '同病相怜', '怜：怜悯，同情。比喻因有同样的遭遇或痛苦而互相同情。', 4),
(524, NULL, '同文共轨', '同文：全国所用的文字相同；共轨：全国车辙阔狭相同。统一文字，统一车辙。比喻国家统一。', 4),
(525, NULL, '头会箕敛', '头会：按人头征税；箕敛：用畚箕装取所征的谷物。形容赋税繁重苛刻。', 4),
(526, NULL, '头没杯案', '头伏在酒杯和桌子间。比喻尽情欢乐，不拘形迹。', 4),
(527, NULL, '投传而去', '传：符信。指弃官而去。', 4),
(528, NULL, '图财害命', '图：谋取。为了劫夺财物，害人性命。', 4),
(529, NULL, '图穷匕见', '图：地图；穷：尽；见：现。比喻事情发展到最后，真相或本意显露了出来。', 4),
(530, NULL, '徒读父书', '徒：只。父书：父亲之言论。白读父亲的兵书。比喻人只知死读书，不懂得运用知识，加以变通。', 4),
(531, NULL, '兔起鹘落', '鹘：打猎用的鹰一类的猛禽。兔子刚跳起来，鹘就飞扑下去。比喻动作敏捷。也比喻绘画或写文章迅捷流畅。', 4),
(532, NULL, '囤积居奇', '囤、居：积聚；奇：稀少的物品。把稀少的货物储藏起来。指商人囤积大量商品，等待高价出卖，牟取暴利。', 4),
(533, NULL, '拖泥带水', '比喻说话做事不干脆利落。', 4),
(534, NULL, '完美无疵', '完善美好，没有缺点。', 4),
(535, NULL, '丸泥封关', '丸泥：一点泥，比喻少；封：封锁。形容地势险要，只要少量兵力就可以把守。', 4),
(536, NULL, '宛转悠扬', '委宛曲折，也指声音悠扬动听。', 4),
(537, NULL, '万世流芳', '好名声永远流传。', 4),
(538, NULL, '王顾左右而言他', '指离开话题，回避难以答复的问题。', 7),
(539, NULL, '网开一面', '把捕禽的网撤去三面，只留一面。比喻采取宽大态度，给人一条出路。', 4),
(540, NULL, '销魂夺魄', '神魂颠倒，失去常态。形容因羡慕或爱好某种事物而着迷。', 4),
(541, NULL, '枵腹从公', '枵腹：空腹，肚饥。指饿着肚子办公家的事。形容一心为公。', 4),
(542, NULL, '小本经营', '小本：资金不多。指小商贩或小手工业者所经营的买卖。也指买卖做得小。', 4),
(543, NULL, '邪不压正', '不正当的、不正派的压不倒正当的、正派的事物。', 4),
(544, NULL, '卸磨杀驴', '磨完东西后，把拉磨的驴卸下来杀掉。比喻把曾经为自己出过力的人一脚踢开。', 4),
(545, NULL, '心安理得', '得：适合。自以为做的事情合乎道理，心里很坦然。', 4),
(546, NULL, '欣喜若狂', '欣喜：快乐；若：好象；狂：失去控制。形容高兴到了极点。', 4),
(547, NULL, '信口开合', '比喻随口乱说一气。', 4),
(548, NULL, '腥闻在上', '腥闻：原指酒肉的腥味，引伸为丑恶的名声。比喻丑名远扬。', 4),
(549, NULL, '行动坐卧', '泛指人的举止和风度。', 4),
(550, NULL, '形色仓皇', '动作匆忙，神色慌张。', 4),
(551, NULL, '兴高采烈', '兴：原指志趣，后指兴致；采：原指神采，后指精神；烈：旺盛。原指文章志趣高尚，言词犀利。后多形容兴致高，精神饱满。', 4),
(552, NULL, '杏脸桃腮', '形容女子容貌美丽。', 4),
(553, NULL, '凶喘肤汗', '形容疲乏到极点。', 4),
(554, NULL, '休明盛世', '休明：美好，清平；盛：兴旺；世：世代。美好清平的兴盛时代。', 4),
(555, NULL, '秀才不出门，全知天下', '旧时认为有知识的人即使待在家里，也能知道外面发生的事情。', 10),
(556, NULL, '臭味相投', '臭味：气味；相投：互相投合。彼此的思想作风、兴趣等相同，很合得来（常指坏的）。', 4),
(557, NULL, '虚晃一枪', '形容佯作进攻，以便退却。', 4),
(558, NULL, '喧宾夺主', '喧：声音大。客人的声音压倒了主人的声音。比喻外来的或次要的事物占据了原有的或主要的事物的位置。', 4),
(559, NULL, '玄酒瓠脯', '饮食只有清水和瓠干。比喻生活清苦。', 4),
(560, NULL, '悬灯结彩', '悬：挂；彩：彩球，彩带。挂着灯笼，系着彩球。形容节日、喜庆的景象。', 4),
(561, NULL, '悬壶问世', '在社会上挂牌行医。原只作“悬壶”，意即行医。', 4),
(562, NULL, '削木为吏', '意思是不能受狱吏的侮辱，即使是木头做的狱吏也不能见他。形容狱吏的凶暴可畏。', 4),
(563, NULL, '学然后知不足，教然后', '学习之后，才知道自己的缺点；教学以后，才知道自己的知识贫乏。', 10),
(564, NULL, '穴居野处', '穴：洞；处：居住。居住在洞里生活在荒野。形容原始人的生活状况。', 4),
(565, NULL, '血海深仇', '血海：形容杀人流血很多。形容仇恨极大、极深。多指人被杀而引起的仇恨。', 4),
(566, NULL, '寻根究底', '追求根底。一般指追问一件事的原由。', 4),
(567, NULL, '寻花问柳', '花、柳：原指春景，旧时亦指娼妓。原指赏玩春天的景色。后旧小说用来指宿娼。', 4),
(568, NULL, '牙白口清', '比喻说话清楚。', 4),
(569, NULL, '烟波钓徒', '烟波：水波渺茫，看远处有如烟雾笼罩；钓：钓鱼。旧指隐逸于渔的人。', 4),
(570, NULL, '妍姿艳质', '形容女子的体态容貌很美。', 4),
(571, NULL, '延颈鹤望', '像鹤一样伸长颈子盼望。比喻盼望心切。', 4),
(572, NULL, '奄奄一息', '奄奄：呼吸微弱的样子。只剩下一口气。形容临近死亡。', 4),
(573, NULL, '眼不见，心不烦', '比喻只要没有看见或不在眼前，也就不会为这操心或烦恼。', 7),
(574, NULL, '厌难折冲', '指能克服困难，抗敌取胜。', 4),
(575, NULL, '阳春白雪', '原指战国时代楚国的一种较高级的歌曲。比喻高深的不通俗的文学艺术。', 4),
(576, NULL, '扬长避短', '发挥或发扬优点或有利条件，克服或回避缺点或不利条件。', 4),
(577, NULL, '养家活口', '维持一家人的生活。', 4),
(578, NULL, '养痈成患', '留着毒疮不去医治，就会成为后患。比喻纵容包庇坏人坏事，结果会遭受祸害。', 4),
(579, NULL, '仰不愧天', '抬头仰望，对天无愧。指没有做过坏事，问心无愧。', 4),
(580, NULL, '夭桃秾李', '比喻年少美貌。多用为对人婚娶的颂辞。', 4),
(581, NULL, '幺麽小丑', '指起不了什么作用的坏人。', 4),
(582, NULL, '遥遥无期', '形容时间还远得很，不知道哪一天。', 4),
(583, NULL, '姚黄魏紫', '姚黄：千叶黄花牡丹，出于姚氏民家；魏紫：千叶肉红牡丹，出于魏仁溥家。原指宋代洛阳两种名贵的牡丹品种。后泛指名贵的花卉。', 4),
(584, NULL, '乐山乐水', '乐：喜爱，爱好。有人喜爱山，有人喜爱水。比喻各人的爱好不同。', 4),
(585, NULL, '叶公好龙', '叶公：春秋时楚国贵族，名子高，封于叶（古邑名，今河南叶县）。比喻口头上说爱好某事物，实际上并不真爱好。', 4),
(586, NULL, '业精于勤', '业：学业；精：精通；于：在于；勤：勤奋。学业精深是由勤奋得来的。', 4),
(587, NULL, '伊于胡底', '伊：句首助词；于：到；胡：何，哪；底：尽头。到什么地步为止（对不好的现象表示感叹）。', 4),
(588, NULL, '一把死拿', '比喻因循守旧，不肯变通。', 4),
(589, NULL, '一分为二', '哲学用语，指事物作为矛盾的统一体，都包含着相互矛盾对立的两个方面。通常指全面看待人或事物，看到积极方面，也看到消极方面。', 4),
(590, NULL, '一狐之腋', '腋：指狐狸腋下的皮毛。一只狐狸腋下的皮毛。比喻珍贵的东西。', 4),
(591, NULL, '一决雌雄', '雌雄：比喻胜负、高下。决一胜负，比个高下。', 4),
(592, NULL, '一刻千金', '一刻时光，价值千金。形容时间非常宝贵。', 4),
(593, NULL, '一鸣惊人', '鸣：鸟叫。一叫就使人震惊。比喻平时没有突出的表现，一下子做出惊人的成绩。', 4),
(594, NULL, '一木难支', '大楼将要倒塌，不是一根木头能够支撑得住的。比喻一个人的力量单薄，维持不住全局。', 4),
(595, NULL, '一年被蛇咬，十年怕井', '比喻在某件事情上吃过苦头，以后一碰到类似的事情就害怕。', 10),
(596, NULL, '一石二鸟', '扔一颗石子打到两只鸟。比喻做一件事情得到两样好处。', 4),
(597, NULL, '一事无成', '连一样事情也没有做成。指什么事情都做不成。形容毫无成就。', 4),
(598, NULL, '一世龙门', '一世：一代；龙门：后汉时李膺有重名，后起的文人有登门拜访的，称之登龙门。称文人所崇仰的人物。', 4),
(599, NULL, '一弹指顷', '手指一弹的时间。比喻时间极短暂。', 4),
(600, NULL, '一网打尽', '比喻一个不漏地全部抓住或彻底肃清。', 4),
(601, NULL, '一泻千里', '泻：水往下直注。形容江河奔流直下，流得又快又远。也比喻文笔或乐曲气势奔放。也形容价格猛跌不止。', 4),
(602, NULL, '一朝权在手，便把令来', '一旦掌了权，就发号施令，指手画脚。', 10),
(603, NULL, '一之谓甚', '甚：过分。做了一次，已经过分。比喻错误不能重犯。', 4),
(604, NULL, '遗臭万年', '遗臭：死后留下的恶名。死后恶名一直流传，永远被人唾骂。', 4),
(605, NULL, '仪静体闲', '形容女子态度文静，体貌素雅。', 4),
(606, NULL, '宜家宜室', '形容家庭和顺，夫妻和睦。', 4),
(607, NULL, '蚁封穴雨', '蚂蚁聚土洞口，是要下大雨的征兆。', 4),
(608, NULL, '以暴易暴', '以：用；易：改变，更换；暴：残暴。用残暴势力代替残暴势力。指统治者换了，暴虐的统治没有改变。', 4),
(609, NULL, '以狸饵鼠', '狸：猫；饵：诱。捉老鼠用猫作诱饵。比喻事情不能成功。', 4),
(610, NULL, '以力服人', '力：权势，武力。用强制手段使人服从。', 4),
(611, NULL, '以小人之心，度君子之', '用卑劣的心意去猜测品行高尚的人。', 10),
(612, NULL, '以子之矛，攻子之盾', '子：对别人的称呼；矛：进攻敌人的刺击武器；盾：保护自己挡住敌人刀箭的牌。比喻拿对方的观点、方法或言论来反驳对方。', 9),
(613, NULL, '意马心猿', '形容心思不定，好象猴子跳、马奔跑一样控制不住。', 4),
(614, NULL, '瘗玉埋香', '指美丽的女子死亡。', 4),
(615, NULL, '衣被群生', '衣被：给人衣穿，比喻加惠于人；群生：众生。比喻恩惠广施。', 4),
(616, NULL, '义薄云天', '正义之气直上高空。形容为正义而斗争的精神极其崇高。', 4),
(617, NULL, '因材施教', '因：根据；材：资质；施：施加；教：教育。指针对学习的人的志趣、能力等具体情况进行不同的教育。', 4),
(618, NULL, '因事制宜', '根据不同的事情，制定适宜的措施。', 4),
(619, NULL, '阴差阳错', '比喻由于偶然的因素而造成了差错。', 4),
(620, NULL, '寅吃卯粮', '这一年吃了下一年的粮。比喻经济困难，收入不够支出，预先支用了以后的进项。比喻指经济困难，入不敷出。', 4),
(621, NULL, '引而不发', '引：拉弓；发：射箭。拉开弓却不把箭射出去。比喻善于启发引导。也比喻做好准备暂不行动，以待时机。', 4),
(622, NULL, '引虎自卫', '引：招引。引来老虎，保卫自己。比喻企图依仗恶人，结果反受其害。', 4),
(623, NULL, '饮马投钱', '比喻为人廉洁，不损公肥私。', 4),
(624, NULL, '莺俦燕侣', '莺、燕：比喻女子；俦：伴侣。指男子的妻室或情侣。', 4),
(625, NULL, '英雄气短', '指有才能的人因沉迷于爱情而丧失进取心。', 4),
(626, NULL, '盈科后进', '泉水遇到坑洼，要充满之后才继续向前流。比喻学习应步步落实，不能只图虚名。', 4),
(627, NULL, '迎风待月', '指男女秘密约会。', 4),
(628, NULL, '郢书燕说', '郢：春秋战国时楚国的都城；书：信；燕：古诸侯国名；说：解释。比喻牵强附会，曲解原意。', 4),
(629, NULL, '忧心如捣', '忧愁得像有东西在捣心一样。形容十分焦急。', 4),
(630, NULL, '游辞巧饰', '用没有根据的话多方掩饰。', 4),
(631, NULL, '游目骋怀', '游目：远眺；骋怀：放开胸怀，往远处想。纵目四望，开阔心胸。', 4),
(632, NULL, '尤物移人', '绝色的女子能移易人的情志。', 4),
(633, NULL, '有过之，无不及', '过：超过；及：赶上。相比之下，只有超过而不会不如。', 7),
(634, NULL, '有一得一', '不加也不减，有多少是多少。', 4),
(635, NULL, '有朝一日', '将来有那么一天。', 4),
(636, NULL, '有枝添叶', '比喻叙述事情或转述别人的话，为了夸大，添上原来没有的内容。', 4),
(637, NULL, '右传之八章', '打了一顿巴掌。', 5),
(638, NULL, '又弱一个', '弱：丧失，减少。又少了一个。表示哀悼人去世。', 4),
(639, NULL, '褎然举首', '褎然：出众的样子；举：推举；首：第一。指人的才能超出同辈。', 4),
(640, NULL, '纡青拖紫', '比喻显贵。', 4),
(641, NULL, '愚不可及', '愚：傻，笨，及：比得上。愚蠢得别人比不上。形容极其愚笨。', 4),
(642, NULL, '余波未平', '指某一事件虽然结束了，可是留下的影响还在起作用。', 4),
(643, NULL, '鱼贯而行', '象游鱼一样一个跟着一个地接连着走。形容一个跟一个单行前进。', 4),
(644, NULL, '予取予求', '予：我。原指从我这里取，从我这里求（财物）。后指任意索取。', 4),
(645, NULL, '雨打风吹', '原指花木遭受风雨摧残。比喻恶势力对弱小者的迫害。也比喻严峻的考验。', 4),
(646, NULL, '与虎谋皮', '跟老虎商量要它的皮。比喻跟恶人商量要他放弃自己的利益，绝对办不到。', 4),
(647, NULL, '与世沉浮', '与：和，同；世：指世人；沉浮：随波逐流。随大流，大家怎样，自己也怎样。', 4),
(648, NULL, '玉不琢，不成器', '琢：雕。玉石不经雕琢，成不了器物。比喻人不受教育、不学习就不能有成就。', 7),
(649, NULL, '玉叶金枝', '封建时代称皇家后裔。', 4),
(650, NULL, '浴血奋战', '形容顽强地拼死战斗。', 4),
(651, NULL, '狱货非宝', '指法官断狱受贿赂，也难逃法网。', 4),
(652, NULL, '鹬蚌持争，渔翁得利', '比喻双方争执不下，两败俱伤，让第三者占了便宜。', 9),
(653, NULL, '冤有头，债有主', '冤有冤头，债有债主。比喻要了解事情，必须找为主的人。', 7),
(654, NULL, '沅茞澧兰', '沅、澧：都是水名；茞、兰：都是香草。比喻高洁的人品或高尚的事物。', 4),
(655, NULL, '源源不断', '形容接连不断。', 4),
(656, NULL, '圆孔方木', '把方木头放到圆孔里去。比喻二者不能投合。', 4),
(657, NULL, '远见卓识', '有远大的眼光和高明的见解。', 4),
(658, NULL, '跃马弯弓', '驰马盘旋，张弓要射。形容摆开架势，准备作战。后比喻故做惊人的姿态，实际上并不立即行动。', 4),
(659, NULL, '月白风清', '形容幽静美好的夜晚。', 4),
(660, NULL, '月下花前', '本指游乐休息的环境。后多指谈情说爱的处所。', 4),
(661, NULL, '云程发轫', '云程：青云万里的路程；发轫：启车行进，比喻事业的开端。旧时祝人前程远大的颂辞。', 4),
(662, NULL, '杂乱无章', '章：条理。乱七八糟，没有条理。', 4),
(663, NULL, '在人矮檐下，怎敢不低', '比喻受制于人，只得顺从。', 10),
(664, NULL, '皂白不分', '不分黑白，不分是非。', 4),
(665, NULL, '择肥而噬', '择：挑选；噬：咬。比喻选择富裕的人进行敲诈勒索。', 4),
(666, NULL, '毡袜裹脚靴', '毡袜、裹脚、靴子，都是穿在脚上的东西。比喻彼此都一样。', 5),
(667, NULL, '张敞画眉', '张敞：汉时平阳人，宣帝时为京兆尹。张敞替妻子画眉毛。旧时比喻夫妻感情好。', 4),
(668, NULL, '长年三老', '古时指船工。', 4),
(669, NULL, '仗马寒蝉', '仗马：皇宫仪仗中的立马。象皇宫门外的立仗马和寒天的知了一样。比喻一句话也不敢说。', 4),
(670, NULL, '朝不保夕', '早晨不能知道晚上会变成什么样子或发生什么情况。形容形势危急，难以预料。', 4),
(671, NULL, '朝气蓬勃', '朝气：早上的空气，引伸为新生向上，努力进取的气象；蓬勃：旺盛的样子。形容充满了生命和活力。', 4),
(672, NULL, '招降纳叛', '原指收容接纳敌方投降叛变过来的人，以扩大自己的势力。现指收罗坏人，结党作恶。', 4),
(673, NULL, '照本宣科', '照：按照；本：书本；宣：宣读；科：科条，条文。照着本子念条文。形容讲课、发言等死板地按照课文、讲稿，没有发挥，不生动。', 4),
(674, NULL, '真赃实犯', '赃物是真的，罪犯是确实的。泛指犯罪的证据确凿。', 4),
(675, NULL, '阵马风樯', '樯：船上用的桅杆。风中的樯帆，阵上的战马。比喻气势雄壮，行动迅速。', 4),
(676, NULL, '正本清源', '正本：从根本上整顿；清源：从源头上清理。从根本上整顿，从源头上清理。比喻从根本上加以整顿清理。', 4),
(677, NULL, '政以贿成', '形容旧社会政治腐败，官场黑暗，不行贿就办不成事。', 4),
(678, NULL, '支离破碎', '支离：零散，残缺。形容事物零散破碎，不完整。', 4),
(679, NULL, '织楚成门', '形容房屋简陋。', 4),
(680, NULL, '知白守黑', '意思是对是非黑白，虽然明白，还当保持暗昧，如无所见。这是道家的消极处世态度。', 4),
(681, NULL, '知法犯法', '知道法律，又违反法律。指明知故犯。', 4),
(682, NULL, '知人之明', '能看出人的品行才能的眼力。', 4),
(683, NULL, '执鞭随蹬', '手里拿着马鞭，跟在马镫旁边。比喻因敬仰而愿意追随在左右。', 4),
(684, NULL, '只此一家，别无分店', '原是一些店铺招揽生意的用语，向顾客表明他没分店，只能在他这一家店里买到某种商品。泛指某种事物只有他那儿有，别处都没有。', 9),
(685, NULL, '助边输财', '边：边防；输：捐献。捐献财物作巩固边防的费用。', 4),
(686, NULL, '妆嫫费黛', '比喻帮助人不看对象，白费力气。', 4),
(687, NULL, '壮士解腕', '勇士手腕被蝮蛇咬伤，就立即截断，以免毒性扩散全身。比喻作事要当机立断，不可犹豫不决。', 4),
(688, NULL, '追奔逐北', '追击败走的敌军。', 4),
(689, NULL, '坠茵落溷', '茵：茵席；溷：厕所。随风而落，有的飘在茵席上，有的落在粪坑里。比喻境遇好坏不同。', 4),
(690, NULL, '拙口钝腮', '比喻嘴笨，没有口才。', 4),
(691, NULL, '灼艾分痛', '比喻兄弟友爱。', 4),
(692, NULL, '斫轮老手', '斫轮：斫木制造车轮。指对某种事情经验丰富的人。', 4),
(693, NULL, '擢发难数', '擢：拔。拔下全部头发，难以数清。形容罪行多得数不清。', 4),
(694, NULL, '资深望重', '资格老，声望高。', 4),
(695, NULL, '趑趄不前', '趑趄：迟疑不敢前进。形容犹豫畏缩不敢前进。', 4),
(696, NULL, '子虚乌有', '子虚：并非真实；乌有：哪有。指假设的、不存在的、不真实的事情。', 4),
(697, NULL, '自拔来归', '拔：摆脱，离开。自觉离开恶劣环境，归向光明。指敌方人员投奔过来。', 4),
(698, NULL, '自给自足', '给：供给。依靠自己的生产，满足自己的需要。', 4),
(699, NULL, '自觉自愿', '自己觉悟而愿意。', 4),
(700, NULL, '自贻伊戚', '贻：遗留；伊：此；戚：忧愁，悲哀。比喻自寻烦恼，自招忧患。', 4),
(701, NULL, '罪不可逭', '罪责不可逃避。', 4),
(702, NULL, '坐不垂堂', '垂堂：近屋檐处。不坐在堂边外面，怕掉到台阶下。比喻不在有危险的地方停留。', 4),
(703, NULL, '坐吃山崩', '只坐着吃，山也要空。指光是消费而不从事生产，即使有堆积如山的财富，也要耗尽。', 4),
(704, NULL, '坐地分赃', '原指盗贼就地瓜分偷盗来的赃物。现多指匪首窝主自己不动手而坐在家里分取同伙偷盗来的财物。', 4),
(705, NULL, '作壁上观', '壁：壁垒。原指双方交战，自己站在壁垒上旁观。后多比喻站在一旁看着，不动手帮助。', 4),
(706, NULL, '阿鼻地狱', '阿鼻：梵语的译音，意译为“无间”，即痛苦无有间断之意。常用来比喻黑暗的社会和严酷的牢狱。又比喻无法摆脱的极其痛苦的境地。', 4),
(707, NULL, '阿狗阿猫', '旧时人们常用的小名。引申为任何轻贱的，不值得重视的人或著作。', 4),
(708, NULL, '阿姑阿翁', '阿：名词的前缀。姑：丈夫的母亲。翁：丈夫的父亲。指公公婆婆。', 4),
(709, NULL, '阿家阿翁', '阿：名词的前缀。家：通“姑”，丈夫的母亲。翁：丈夫的父亲。指公公婆婆。', 4),
(710, NULL, '阿娇金屋', '阿娇：指汉武帝刘彻的姑母（长公主刘娇）的女儿。原指汉武帝刘彻要用金屋接纳阿娇为妇。这里泛指美丽高贵的女子。', 4),
(711, NULL, '阿猫阿狗', '旧时人们常用的小名。引申为任何轻贱的，不值得重视的人或著作。', 4),
(712, NULL, '阿毗地狱', '阿毗：梵语的译音，意译为“无间”，即痛苦无有间断之意。常用来比喻黑暗的社会和严酷的牢狱。又比喻无法摆脱的极其痛苦的境地。', 4),
(713, NULL, '阿平绝倒', '以之比喻对对方的言论极为佩服。亦用为讥讽言论极为乖谬，常贻笑大方。', 4),
(714, NULL, '阿党比周', '指相互勾结，相互偏袒，结党营私。', 4),
(715, NULL, '阿党相为', '阿党：偏袒、偏私一方。为了谋求私利相互偏袒、包庇。', 4),
(716, NULL, '阿弥陀佛', '佛教语，信佛的人用作口头诵颂的佛号，表示祈祷祝福或感谢神灵的意思。', 4),
(717, NULL, '阿其所好', '阿：曲从；其：他的；好：爱好。指为取得某人的好感而迎合他的爱好。', 4),
(718, NULL, '阿时趋俗', '阿：迎合。迎合时尚和世俗。', 4),
(719, NULL, '阿世盗名', '阿世：曲意博取世人的欢心。用博取世人的欢心的方法来窃取名誉。', 4),
(720, NULL, '阿世媚俗', '指曲意迎合当时社会的陈规陋俗。', 4),
(721, NULL, '阿世取容', '指迎合世俗，取悦于人。', 4),
(722, NULL, '阿意取容', '指曲从其意，以取悦于人。', 4),
(723, NULL, '阿谀谄媚', '阿谀：迎合别人的意思，向人讨好；谄媚：巴结，奉承。说话做事迎合别人的心意，竭力向人讨好。', 4),
(724, NULL, '阿谀逢迎', '阿谀：用言语恭维别人；逢迎：迎合别人的心意。奉承，拍马，讨好别人。', 4),
(725, NULL, '阿谀奉承', '阿谀：用言语恭维别人；奉承：恭维，讨好。曲从拍马，迎合别人，竭力向人讨好。', 4),
(726, NULL, '阿谀取容', '阿谀：曲意逢迎；取容：取悦于人。谄媚他人，以取得其喜悦。', 4),
(727, NULL, '哀哀父母', '可哀呀可哀，我的父母啊！原指古时在暴政下的人民终年在外服劳役，对父母病痛、老死不能照料而悲哀。', 4),
(728, NULL, '哀哀欲绝', '哀哀：极度悲哀；绝：断气，死。形容极其悲痛。', 4),
(729, NULL, '哀兵必胜', '原意是力量相当的两军对阵，悲愤的一方获得胜利。后指因受欺侮而奋起抵抗的军队，必定能取胜。', 4),
(730, NULL, '哀而不伤', '哀：悲哀；伤：伤害。忧愁而不悲伤，形容感情有节制；另形容诗歌、音乐优美雅致，感情适度。比喻做事没有过头也无不及。', 4),
(731, NULL, '哀感天地', '形容极其哀痛，使天地都为之感动。', 4),
(732, NULL, '哀感顽艳', '顽：愚顽。原意是指内容凄切，文辞华丽，使愚笨和聪慧的人同样受感动。后多用来指艳情的小说、戏曲、电影中的感人情节。', 4),
(733, NULL, '哀感中年', '形容人到中年对亲友离别的伤感情绪。', 4),
(734, NULL, '哀告宾服', '宾服：古指诸侯或边远部落按时朝贡，表示依服、服从。后泛指归顺、服从。指哀求饶恕，愿意服从。', 4),
(735, NULL, '哀鸿遍地', '哀鸿：哀鸣的鸿雁。比喻啼饥号寒的灾民。比喻在天灾人祸中到处都是流离失所、呻吟呼号的饥民。', 4),
(736, NULL, '哀鸿遍野', '哀鸿：哀鸣的鸿雁。比喻啼饥号寒的灾民。比喻在天灾人祸中到处都是流离失所、呻吟呼号的饥民。', 4),
(737, NULL, '哀毁骨立', '哀：悲哀；毁：损坏身体；骨立：形容极瘦，只剩下骨架。旧时形容在父母丧中因过度悲伤而瘦得只剩一把骨头。', 4),
(738, NULL, '哀毁瘠立', '旧时形容在父母丧中因过度悲伤而瘦得只剩一把骨头。', 4),
(739, NULL, '哀梨蒸食', '将哀梨蒸着吃。比喻不识货。糊里糊涂地糟蹋好东西。', 4),
(740, NULL, '哀莫大于心死', '心死：指心像死灰的灰烬。指最可悲哀的事，莫过于思想顽钝，麻木不仁。', 6),
(741, NULL, '哀声叹气', '因伤感郁闷或悲痛而发出叹息的声音。', 4),
(742, NULL, '哀丝豪竹', '丝：指弦乐器；竹：指管乐器；豪竹：粗大的竹管制成的乐器。形容管弦乐声的悲壮动人。', 4),
(743, NULL, '哀思如潮', '哀伤的思绪如同潮涌一般。形容极度悲痛。', 4),
(744, NULL, '哀天叫地', '哀：悲哀。悲哀地呼天喊地。形容悲痛至极。', 4),
(745, NULL, '哀痛欲绝', '伤心得要死。形容悲痛到了极点。', 4),
(746, NULL, '唉声叹气', '因伤感郁闷或悲痛而发出叹息的声音。', 4),
(747, NULL, '挨冻受饿', '指无衣无食的穷困境况。', 4),
(748, NULL, '挨风缉缝', '比喻为了达到某种目的而找门路。', 4),
(749, NULL, '挨家挨户', '每家每户，户户不漏。挨，依次，顺次。', 4),
(750, NULL, '挨肩并足', '形容人群拥挤。', 4),
(751, NULL, '挨肩擦膀', '指身体相贴近。也形容人群拥挤。', 4),
(752, NULL, '挨肩擦背', '肩挨肩，背擦背。形容人多拥挤。', 4),
(753, NULL, '挨肩擦脸', '挨：靠近。擦，接触。形容狎昵之状。', 4),
(754, NULL, '挨肩搭背', '挨肩：肩和肩相靠。搭背：手搭在别人背上。形容极其亲昵的样子。', 4),
(755, NULL, '挨肩迭背', '形容人群拥挤。', 4),
(756, NULL, '挨肩叠背', '形容人群拥挤。', 4),
(757, NULL, '挨门挨户', '挨：按照顺序。按照住户的顺序一家也不漏。同“挨门逐户”。', 4),
(758, NULL, '挨门逐户', '挨家挨户，一家也没有漏掉。', 4),
(759, NULL, '挨三顶五', '形容人多，连接不断。', 4),
(760, NULL, '挨山塞海', '形容人极多而拥挤不堪。', 4),
(761, NULL, '捱风缉缝', '指多方钻营，找门路。', 4),
(762, NULL, '捱三顶四', '形容人群拥挤，接连不断。', 4),
(763, NULL, '捱三顶五', '形容人群拥挤，接连不断。', 4),
(764, NULL, '嗳声叹气', '因伤感、烦闷或痛苦而发出叹息的声音。', 4),
(765, NULL, '矮人观场', '比喻只知道附和别人，自己没有主见。也比喻见识不广。', 4),
(766, NULL, '矮人看场', '比喻只知道附和别人，自己没有主见。也比喻见识不广。', 4),
(767, NULL, '矮人看戏', '比喻只知道附和别人，自己没有主见。也比喻见识不广。', 4),
(768, NULL, '矮子观场', '比喻只知道附和别人，自己没有主见。也比喻见识不广。', 4),
(769, NULL, '矮子看戏', '比喻只知道附和别人，自己没有主见。也比喻见识不广。', 4),
(770, NULL, '蔼然可亲', '蔼然：对人和善的样子。形容态度和气，使人愿意接近。', 4),
(771, NULL, '蔼然仁者', '对人和善的有仁德的人。', 4),
(772, NULL, '爱别离苦', '佛家语，指亲爱的人离别的痛苦。', 4),
(773, NULL, '爱博而情不专', '对人或事物的喜爱很广泛，而感情不能专一。', 6),
(774, NULL, '爱不忍释', '释：放下。对所喜欢的物品，爱得拿在手里久久不肯放下。', 4),
(775, NULL, '爱不释手', '释：放下。喜爱得舍不得放手。', 4),
(776, NULL, '爱才如渴', '才：贤才，人才。爱慕贤才，急欲求得，就象口渴急于喝水一样。', 4),
(777, NULL, '爱才如命', '才：贤才，人才。爱慕贤才，急欲求得，就象生命一样重要。', 4),
(778, NULL, '爱才若渴', '才：贤才，人才。爱慕贤才，急欲求得，就象口渴急于喝水一样。', 4),
(779, NULL, '爱财如命', '把钱财看得跟生命一样重要。形容极端吝啬。', 4),
(780, NULL, '爱富嫌贫', '嫌弃贫穷，喜爱富有。指对人的好恶以其贫富为准。', 4),
(781, NULL, '爱国如家', '指帝王像爱抚自己的家室一样爱国爱民。', 4),
(782, NULL, '爱鹤失众', '比喻因小失大。', 4),
(783, NULL, '爱礼存羊', '由于爱惜古礼，不忍使它废弛，因而保留古礼所需要的祭羊。比喻为维护根本而保留有关仪节。', 4),
(784, NULL, '爱毛反裘', '反裘：反穿皮衣，指皮毛朝里。古时穿皮毛衣服，毛的一面向外。比喻不重视根本，轻重倒置。', 4),
(785, NULL, '爱民如子', '旧时称赞某些统治者爱护百姓，就象爱护自己的子女一样。', 4),
(786, NULL, '爱莫能助', '爱：爱惜；莫：不。虽然心中关切同情，却没有力量帮助。', 4),
(787, NULL, '爱莫之助', '爱：爱惜；莫：不。虽然心中关切同情，却没有力量帮助。', 4),
(788, NULL, '爱钱如命', '某些人贪爱钱财就像吝啬生命一样。形容十分贪婪、刻薄。', 4),
(789, NULL, '爱人好士', '爱护、重视人才。', 4),
(790, NULL, '爱人利物', '爱护人民，力求有利于人民。', 4),
(791, NULL, '爱人以德', '爱人：爱护别人；德：德行。按照道德标准去爱护人。泛指对人不偏私偏爱，不姑息迁就。', 4),
(792, NULL, '爱日惜力', '珍惜时间，不虚掷精力。', 4),
(793, NULL, '爱如己出', '像对待亲生子女那样地爱护。', 4),
(794, NULL, '爱如珍宝', '形容十分喜欢。', 4),
(795, NULL, '爱生恶死', '恶：厌恶。喜爱生存，厌恶死亡。', 4),
(796, NULL, '爱素好古', '指爱好朴质，不趋时尚。', 4),
(797, NULL, '爱屋及乌', '因为爱一个人而连带爱他屋上的乌鸦。比喻爱一个人而连带地关心到与他有关的人或物。', 4),
(798, NULL, '爱惜羽毛', '羽毛：比喻人的声望。比喻为珍惜自己的名声，行事十分谨慎。', 4),
(799, NULL, '爱则加诸膝，恶则坠诸渊', '加诸膝：放在膝盖上；坠诸渊：推进深渊里。意指不讲原则，感情用事，对别人的爱憎态度，全凭自己的好恶来决定。', 11),
(800, NULL, '爱憎分明', '憎：恨。爱和恨的立场和态度十分鲜明。', 4),
(801, NULL, '爱之欲其生', '指喜欢一个人时总想叫他活着。指极度地凭个人爱憎对待人。', 5),
(802, NULL, '爱之欲其生，恶之欲其死', '喜爱他时，总想叫他活着；讨厌他时，总想叫他死掉。指极度地凭个人爱憎对待人。', 11),
(803, NULL, '碍口识羞', '碍口：说不出口。指怕羞而不说话。', 4),
(804, NULL, '碍难从命', '碍：妨碍；从：听从。由于有所妨碍，难以遵从吩咐，是委婉拒绝对方要求的话。', 4),
(805, NULL, '碍手碍脚', '碍：阻碍。妨碍别人做事。', 4),
(806, NULL, '碍足碍手', '碍：阻碍。妨碍别人做事。同“碍手碍脚”。', 4),
(807, NULL, '暧昧不明', '暧昧：含糊，不清楚。指不清不楚，不明不白。形容态度不明朗，关系不鲜明。', 4),
(808, NULL, '暧昧之情', '行为不光明，内有不可告人的隐情。也指男女互相爱悦可能的私事。', 4),
(809, NULL, '安安稳稳', '形容十分安定稳当。', 4),
(810, NULL, '安邦定国', '邦：泛指国家。使国家安定巩固。', 4),
(811, NULL, '安邦治国', '使国家安定太平。', 4),
(812, NULL, '安不忘危', '在安全的时候不忘记危难。意思是时刻谨慎小心，提高警惕。', 4),
(813, NULL, '安步当车', '安：安详，不慌忙；安步：缓缓步行。以从容的步行代替乘车。', 4),
(814, NULL, '安常处顺', '安：习惯于；处：居住，居于；顺：适合，如意。习惯于平稳的日子，处于顺利的境遇中。', 4),
(815, NULL, '安常履顺', '习惯于平稳的日子，处于顺利的境遇中。同“安常处顺”。', 4),
(816, NULL, '安常守分', '分：本分，自己分内的。习惯于平稳的生活，安守本分。', 4),
(817, NULL, '安常守故', '习惯于日常的平稳生活，保守旧的一套。指守旧不知变革。', 4),
(818, NULL, '安常习故', '习：习惯；故：旧例。安于常规，习惯旧例。形容按照常规和老套办事，守旧而不思变革。', 4),
(819, NULL, '安车蒲轮', '让被征请者坐在安车上，并用蒲叶包着车轮，以便行驶时车身更为安稳。表示皇帝对贤能者的优待。', 4),
(820, NULL, '安堵乐业', '安堵：相安，安定。犹言安居乐业。', 4),
(821, NULL, '安堵如故', '堵：墙壁，居所。故：原来的。像原来一样相安无事。', 4),
(822, NULL, '安分守己', '分：本分。规矩老实，守本分，不做违法的事。', 4),
(823, NULL, '安分守已', '规矩老实，守本分，不做违法的事。', 4),
(824, NULL, '安分知足', '安于本分，对自己所得到的待遇知道满足。', 4),
(825, NULL, '安份守己', '规矩老实，守本分，不做违法的事。', 4),
(826, NULL, '安富恤贫', '恤：救济。使富有的人安定，贫穷的人得到救济。', 4),
(827, NULL, '安富恤穷', '安定富有者，振济贫苦者。指统治者治国安民之道。', 4),
(828, NULL, '安富尊荣', '安：安逸；富：富有；尊：尊贵；荣：荣耀。安定富足，尊贵荣华。也指安于富裕安乐的生活。', 4),
(829, NULL, '安故重迁', '指安于旧俗，不轻易改变。', 4),
(830, NULL, '安国富民', '使国家安定，使人民富裕。', 4),
(831, NULL, '安国宁家', '指治理国家。', 4),
(832, NULL, '安魂定魄', '魂、魄：人的灵气、精神。指使人心安定。', 4),
(833, NULL, '安家乐业', '犹安居乐业。指安定愉快地生活和劳动。', 4),
(834, NULL, '安家立业', '安置家庭，创立基业或事业。也指长期在一个地方劳动和生活。', 4),
(835, NULL, '安家落户', '安：安置；落户：指定居。到一个新地方安家，长期居住。', 4),
(836, NULL, '安居乐业', '安：安定；乐：喜爱，愉快；业：职业。指安定愉快地生活和劳动。', 4),
(837, NULL, '安老怀少', '安：安顿；怀：关怀。尊重老人，使其安逸；关怀年轻人，使其信服。', 4),
(838, NULL, '安乐窝', '泛指安静舒适的住处。', 3),
(839, NULL, '安良除暴', '安抚善良的人，铲除强暴的人。', 4),
(840, NULL, '安眉带眼', '长了眉毛，有了眼睛。意思是同样是一个人。', 4),
(841, NULL, '安民告示', '安定人心的布告。现也指把要商量的或要办的事情预先通知大家。', 4),
(842, NULL, '安内攘外', '攘：排除。原就药的疗效而言。后多指安定内部，排除外患。', 4),
(843, NULL, '安贫乐道', '道：主张，思想。安于贫穷，以坚持自己的信念为乐。旧时士大夫所主张的为人处世之道。', 4),
(844, NULL, '安贫乐贱', '安于贫贱，并以此为乐。', 4),
(845, NULL, '安贫守道', '安于贫穷，恪守信仰。', 4),
(846, NULL, '安然如故', '还象原来那样安安稳稳。', 4),
(847, NULL, '安然无事', '犹言平安无事。', 4),
(848, NULL, '安然无恙', '恙：病。原指人平安没有疾病。现泛指事物平安未遭损害。', 4),
(849, NULL, '安忍无亲', '安忍：习于残忍，不以为异。安心于做残忍的事情，因而无所谓亲人。', 4),
(850, NULL, '安如盘石', '安：安稳；盘石：大石头。像盘石一样安稳不动。形容非常稳固，不可动摇。', 4),
(851, NULL, '安如磐石', '磐石：大石头。如同磐石一般安然不动。形容非常稳固。', 4),
(852, NULL, '安如太山', '形容极其平安稳固。泰，也写作“太”。同“安如泰山”。', 4),
(853, NULL, '安如泰山', '形容象泰山一样稳固，不可动摇。', 4),
(854, NULL, '安若泰山', '形容极其平安稳固。同“安如泰山”。', 4),
(855, NULL, '安身乐业', '指安稳快乐地过日子。', 4),
(856, NULL, '安身立命', '安身：在某处安下身来；立命：精神有所寄托。指生活有着落，精神有所寄托。', 4),
(857, NULL, '安身为乐', '身子安定就是快乐。', 4),
(858, NULL, '安身之处', '指得以立足容身的地方。', 4),
(859, NULL, '安身之地', '存身的地方。批在某地居住、生活，或以某地作为建业的根基。', 4),
(860, NULL, '安神定魄', '魄：魂魄。安定心神，恢复神智。', 4),
(861, NULL, '安生服业', '指平稳地生活，安心地从事自己的生业。', 4),
(862, NULL, '安生乐业', '犹安居乐业。指安定愉快地生活和劳动。', 4),
(863, NULL, '安时处顺', '安于常分，顺其自然。形容满足于现状。', 4),
(864, NULL, '安室利处', '指安全便利的处所。', 4),
(865, NULL, '安适如常', '安静而舒适，象往常一样。指经过某种变动后，恢复了正常，使人感到舒适。', 4),
(866, NULL, '安土乐业', '安居本土，愉快地从事自己的职业。', 4),
(867, NULL, '安土重旧', '指留恋故土，不轻易改变旧俗。', 4),
(868, NULL, '鞍不离马，甲不离身', '甲：铠甲，古人打仗时穿的护身衣。马不卸鞍，人不解甲。处于高度警惕状态。', 9),
(869, NULL, '鞍不离马背，甲不离将身', '甲：铠甲。马不卸鞍，人不解甲。处于高度警惕状态。', 11),
(870, NULL, '鞍马劳顿', '顿：困顿。骑马赶路过久，劳累疲困。形容旅途劳累。', 4),
(871, NULL, '鞍马劳倦', '指长途跋涉或战斗中备尝困乏。', 4),
(872, NULL, '鞍马劳困', '指长途跋涉或战斗中备尝困乏。', 4),
(873, NULL, '鞍马劳神', '形容旅途劳累。同“鞍马劳顿”。', 4),
(874, NULL, '鞍马之劳', '鞍马：鞍子和马，指长途跋涉或打仗。形容旅途或战斗的劳苦。', 4),
(875, NULL, '鞍前马后', '马前马后。指追随左右。', 4),
(876, NULL, '岸谷之变', '比喻政治上的重大变化。', 4),
(877, NULL, '岸然道貌', '指严肃的神态。', 4),
(878, NULL, '按兵不动', '按：止住。使军队暂不行动。现也比喻暂不开展工作。', 4),
(879, NULL, '按兵不举', '犹按兵不动。', 4),
(880, NULL, '按兵束甲', '按兵：军队停止行进。甲：铠甲，泛指武器装备。止兵不前，捆起铠甲。指解除武装，停战言降。', 4),
(881, NULL, '按步就班', '指按照条理或遵循一定的程序。参见“按部就班”。', 4),
(882, NULL, '按部就班', '部、班：门类，次序；就：归于。按照一定的步骤、顺序进行。也指按老规矩办事，缺乏创新精神。', 4),
(883, NULL, '按堵如故', '形容秩序良好，百姓和原来一样安居乐业。', 4),
(884, NULL, '按甲寝兵', '收拾起铠甲武器。比喻停止军事行动。', 4),
(885, NULL, '按甲休兵', '收拾起铠甲武器。比喻停止军事行动。', 4),
(886, NULL, '按名责实', '按照事物名称，要求与实相符。', 4),
(887, NULL, '按捺不住', '按捺：压抑，忍耐。心里急燥，克制不住。', 4),
(888, NULL, '按辔徐行', '辔：马缰绳。轻轻按着缰绳，让马慢慢地走。', 4),
(889, NULL, '按图索骥', '索：找；骥：良马。按照画像去寻求好马。比喻墨守成规办事；也比喻按照线索去寻求。', 4),
(890, NULL, '按图索骏', '指按照画像去寻求好马。比喻墨守成规办事；也比喻按照线索去寻求。参见“按图索骥”。', 4),
(891, NULL, '按行自抑', '按：克制。约束自己的行为。', 4),
(892, NULL, '案兵束甲', '放下兵器，捆束铠甲。指停止作战。', 4),
(893, NULL, '案牍劳形', '案牍：公文。文书劳累身体。形容公事繁忙。', 4);
