(11826, NULL, '交淡若水', '指道义上的往来。', 4),
(11827, NULL, '交颈并头', '形容许多人聚集在一起。', 4),
(11828, NULL, '交口称誉', '交：一齐，同时。异口同声地称赞。', 4),
(11829, NULL, '交口称赞', '交：一齐，同时。异口同声地称赞。', 4),
